INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (444, 'JR', '', 'Описание Трусы CCM TACKS 1052 модель начального уровня, обеспечивает надежную защиту и комфорт. Внешний чехол пошит из износоустойчивого нейлона и имеет эластичные вставки Flex Zones, которые позволяют защитному снаряжению свободно двигаться вместе с телом хоккеиста. Расширенный элемент защиты позвоночника выполнен из пены Comfort и имеет жесткую, пластиковую вставку для дополнительной защиты. Сегментированный элемент защиты копчика соединяется с протектором защиты позвоночника, образуя надежную защиту. Формованные элементы из пластика, покрытие вспененным материалом, создают опоясывающую защиту области почек. Для защиты тазобедренных суставов используются элементы, выполненные по технологии JDP, которая рассеивает энергию удара и перераспределяет ее от сустава к соседним мышцам. Набедренные щитки выполнены из жесткого, формованного пластика с внутренним покрытием из пены Comfort. Дополнительный слой пены Comfort и сетчатый материал используются в качестве подкладочного слоя и обеспечивают мягкую и комфортную посадку трусов. Нейлоновый ремень позволяет зафиксировать трусы на талии. Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/cb7/400_400_140cd750bba9870f18aada2478b24840a/4baae512_0bd1_11e6_9fde_001fc6b58537_1cd196a4_1b4a_11e6_be9d_96199a2fcfde.jpg?158329876639252', null, 'TACKS 1052', 2, 446, 9, 'C:\Users\vinto/images/Трусы/CCM_TACKS 1052_JR.png');
