INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (37, 'YTH', '', 'Описание Коньки BAUER VAPOR 2X YTH, профессиональная детская модель линейки VAPOR 2019 года. При создании коньков использовалась система ACCELFLEX SYSTEM, эта система обеспечивает высокую эффективность и дополнительную гибкость коньков. Ботинок выполнен из композитного материала 3D LASTED FIBER COMPOSITE, который обладает легким весом и хорошими защитными функциями. Язычок из войлока плотностью 30 унций усилен пеной высокой плотности в области плюсневой кости. Внутренняя подкладка LOCK-FIT хорошо отводит влагу и быстро сохнет. Так же для максимального комфорта в области лодыжки используется подкладка COMFORT EDGE Особенности: ✅Уровень – Профессиональный ✅Внешний материал – 3D LASTED FIBER COMPOSITE ✅Внутренний материал – LOCK-FIT ✅Язык – Войлочный плотностью 30 унций с дополнительной вставкой из пены высокой плотностью ✅Стелька – LOCK-FIT ✅Термоформуемость – Частичная формовка в области лодыжки ✅Подошва – TPR ✅Стакан – TUUK LIGHTSPEED PRO II ✅Лезвие – TUUK LS1 Характеристики Возрастная группа YTH Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6ff/400_400_140cd750bba9870f18aada2478b24840a/0bc47424_980e_11e9_aa9a_a4bf0158c9f5_81136a21_99d2_11e9_aa9a_a4bf0158c9f5.jpg?158328261763591', null, 'VAPOR 2X', 1, 39, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 2X_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (48, 'SR', '', 'Описание Коньки BAUER SUPREME S180 профессиональная модель в новой линейке SUPREME 2016 года. Ботинок имеет анатомическую форму, которая точно повторяет все изгибы стопы и придает плотную, комфортную посадку ботинку еще до процесса термоформовки. Композитный материал 3D-Lasted Curv, используемый в производстве ботинок этой модели, придает ботинкам жесткость профессионального уровня, способствует более мощному толчку, увеличивая эффективность шага. Ботинок полностью термоформируемый, что позволяет получить комфортную, плотную, индивидуальную посадку. Гибкий задник ботинка, защищающий ахиллово сухожилие Free Flex Tendon Guard, выполнен из толстого пластика, обладает хорошей пространственной жесткостью, а так же обеспечивает максимальный диапазон движений ноги в голеностопном суставе. Внешняя, усиленная пяточная чаша обеспечивает дополнительную защиту заднего отдела стопы. Обивка в области голеностопного сустава выполнена из легкой пены с эффектом памяти и плотно фиксирует стопу внутри ботинка. Внутренний язычок Form Fit+, состоящий из трех частей, имеет внутреннюю набивку из пены высокой плотности и армированную защиту плюсневой кости и покрыт белым войлоком плотностью 48 oz / 30 oz. Легкая конструкция язычка обеспечивает профессиональную защиту плюсневой кости и защиту подъема от передавливания шнурками. Внутренняя подкладка выполнена из стойкого к истиранию материала Hydra-Max Mesh, быстро отводит влагу от ноги и способствует быстрому высыханию коньков. Внутренняя стелька Form-Fit+ выполнена из пены средней плотности, имеет покрытие из гидрофобного материала и дренажные отверстия для отвода влаги и излишков тепла. На стельке расположены участки с противоскользящим покрытием Stabilizer Grip, что исключает смещение стопы в ботинке и способствует оптимальной передачи энергии при движении. Подошва выполнена из термопластичной резины с повышенной жесткостью. На коньках стоят стаканы Tuuk LightSpeed Edge, которые выше на 3 мм, чем традиционные стаканы, что позволяет увеличить угол атаки и уменьшить радиус разворота и, как результат, улучшить маневренность игрока. Данные стаканы так же снабжены системой быстрой смены лезвий Trigger System. В данной модели установлены профессиональные лезвия из нержавеющей стали Tuuk LightSpeed LS2. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/170/400_400_140cd750bba9870f18aada2478b24840a/d226c771_136c_11e6_b103_85ec36a0f0cc_9d0e0e68_391e_11e6_bec3_dba2b096c3df.jpg?158329604450346', null, 'SUPREME S180', 1, 50, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S180_SR.png');
