INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (484, 'SR', '', 'Описание Трусы BAUER VAPOR 1X топовая модель в линейке Vapor, новинка 2016 года выполнена с использованием новейших технологий и создает высокий уровень защиты, обеспечивая полевому игроку максимальную мобильность. Чехол трусов скомбинирован из прочного нейлона плотностью 840 Den и эластичного материала для обеспечения максимальной мобильности. Эластичные, хорошо вентилируемые, клинья и зоны гибкости располагаются в паховой области, вдоль внутренней части штанин и задней части трусов. Высокопрочный нейлон усиливает боковые части трусов, располагаясь вдоль бедер. Защитный щиток, закрывающий позвоночник, имеет конструкцию Free Flex Spine и слегка наклонен вперед, что позволяет ему двигаться независимо от других элементов защиты, обладая высокой подвижностью и всегда находиться там, где он должен быть. Этот защитный щиток выполнен из композитного материала Curv, дополнен пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами, не впитывает влагу, и армирован пластиковой вкладкой. Такое сочетание позволяет облегчить защитное снаряжение и обеспечить максимально высокий уровень защиты. Аналогичное строение с использованием сочетания композитного материала Curv и пены Aerolite имеют защитные элементы, закрывающие область почек, копчик, область таза и тазобедренных суставов. Щитки, закрывающие бедра, имеют эргономичную конструкцию, и так же выполнены из композитного материала Curv и пены Aerolite, что создает надежную и легкую защиту. По внутренней стороне трусов проходит регулируемый, внутренний, нейлоновый пояс, который усилен пеной средней плотности. На талии, под поясом располагается молния, раскрыв которую можно увеличить длину трусов на 1" (2,5 см), что делает данную модель особенно удобной для высоких игроков. Внутренняя, гидрофобная подкладка из сетчатого материала выполнена с применением технологии 37.5, которая позволяет использовать тепло тела для быстрого испарения влаги. А так же подкладка обладает противоскользящими свойствами и не поглощает запахи. На внутренней стороне штанин внизу расположены молнии. Трусы фиксируются на теле с помощью нейлонового ремня с прочным карабином и шнуровки. Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/064/400_400_140cd750bba9870f18aada2478b24840a/aa6bc99e_142b_11e6_b103_85ec36a0f0cc_17ac9b2b_2ad4_11e6_beb1_8807972027df.jpg?158328125530478', null, 'VAPOR 1X', 1, 486, 9, 'C:\Users\vinto/images/Трусы/BAUER_VAPOR 1X_SR.png');
