INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (197, 'JR', '', 'Описание Раковина вратаря CCM 500 Вешняя отделка раковины выполнена из мягких, воздухопроницаемых материалов, отличающихся высокой износоустойчивостью. Раковина комплектуется стандартной, защитной чашей из формованного пластика, которая покрывается мягким, вспененным материалом, что создает эффективную защиту области паха и способствует комфортному прилеганию чаши к телу. Дополнительную защиту для паха и внутренней области бедра создают специальные накладки из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт. Передняя панель, защищающая нижнюю часть живота и область тазобедренных суставов, выполнена из вспененного материала и имеет удобную, эргономичную форму. Сегментированное строение передней панели не ограничивает движения вратаря и способствует комфортной и плотной посадке раковины на теле. Подходит примерно на возраст от 5 до 10 лет Характеристики Возрастная группа JR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/a6e/f5ce8294_0bd0_11e6_9fde_001fc6b58537_bee7e805_392b_11e6_bec4_de6fc43a47df.jpg?1557604715157870', null, '500', 2, 199, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_500_JR.png');
