INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (167, 'SR', '', 'Описание Трусы вратаря CCM PREMIER PRO LE, топовая модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Для защиты бедра используется формованный пластик с пеной низкой плотности. Для максимальной защиты в области тазобедренных суставов используется материал D3O, который поглощает энергию удара. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Чехол трусов выполнен из высокопрочного абразивного нейлона плотностью 420 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Элитный ✅Защита бедра – Пластик; Пена низкой плотности ✅Защита тазобедренная – D3O ✅Защита позвоночника - EVA ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Высокопрочный абразивный нейлон 420 ден Характеристики Возрастная группа SR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3ad/400_400_140cd750bba9870f18aada2478b24840a/fbfef431_8ea0_11e9_aa99_a4bf0158c9f5_c45c6fa7_c9e1_11e9_aa9a_a4bf0158c9f5.jpg?158328556645936', null, 'PREMIER PRO LE', 2, 169, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_PREMIER PRO LE_SR.png');
