INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (475, 'SR', '', 'Описание Трусы BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Основные особенности этих трусов, то что они имеют легкий вес, но при этом хорошо защищены. Внешний материал трусов выполнен из нейлона плотностью 840 ден., так же как в 1X LITE. В стратегически важных местах использована пена высокой плотностью HYPERLITE. Внутренний материал THERMO MAX, который обработан по технологии SANITIZED. Особенности: • Уровень – Профессиональный • Удобство – Клиновидное • Защита позвоночника –Пена HYPERLITE; FREE FLEX • Защита почек – Пена HYPERLITE • Защита тазобедренная – Пена; пластиковые вставки • Защита бедра – Пена; пластиковые вставки • Подкладка –THERMO MAX; SANITIZED® • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/962/400_400_140cd750bba9870f18aada2478b24840a/68b5a798_6ae3_11e8_995b_3085a97fbce9_05ea5bdb_6fb9_11e8_995c_3085a97fbce9.jpg?158331307944062', null, 'VAPOR X900 LITE', 1, 477, 9, 'C:\Users\vinto/images/Трусы/BAUER_VAPOR X900 LITE_SR.png');
