INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (477, 'JR', '', 'Описание Трусы BAUER NEXUS 1N флагманская модель в линейке Nexus 2016 года. Чехол трусов пошит из высокопрочного и легкого нейлона плотностью 840 Den. Дополнительные эластичные вставки и ластовица в чехле позволяют игроку свободно двигаться и сгибаться, а так же способствуют дополнительной воздухопроницаемости защитного снаряжения. Защитный щиток, закрывающий позвоночник, выполнен с применением технологииSeven+ и пены высокой плотности, благодаря чему формованная пена щитка эффективно поглощает силу удара, сохраняя свою первоначальную форму и свои защитные функции после многочисленных попаданий шайбой или клюшкой. Плавающая конструкция щитка обеспечивает большую подвижность защиты спины. При этом легкость используемой в щитке пены отлично сочетается с ее высокими защитными свойствами. Сегментированный элемент защиты копчика выполнен из пены высокой плотности. Над талией расположена формованная защита области почек, которая выполнена из пены высокой плотности. Тазобедренные суставы защищены куполообразными чашами, которые плотно прилегают к телу и создают надежную защиту благодаря использованию при их производстве формованного полиэтилена и пены Comfort. Эргономичные элементы защиты бедра с внутренним наполнением из пены средней плотности имеют двухсоставную конструкцию, обеспечивая максимальное покрытие и удобную посадку. Внутренняя подкладка выполнена из микрофибры с применением технологии 37.5, которая использует тепло тела хоккеиста для быстрого и эффективного испарения влаги. Специальная обработка материала подкладки устраняет появление неприятных запахов в процессе эксплуатации защиты. Широкий проложенный пеной пояс создает дополнительную защиту талии. Внизу штанин с внутренней стороны имеются дополнительные молнии, позволяющие увеличить ширину штанин. Расширенная система подгонки позволяет увеличить длину трусов на 1" (2,5 см). Трусы крепятся на теле игрока с помощью шнуровки и нейлонового пояса с прочным карабином. Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/586/400_400_140cd750bba9870f18aada2478b24840a/0aaf70d5_4f19_11e6_b50e_e0cb4ee6cf35_9c2281a7_54c1_11e6_9c01_3085a97fbce9.jpg?158331254832510', null, 'NEXUS 1N', 1, 479, 9, 'C:\Users\vinto/images/Трусы/BAUER_NEXUS 1N_JR.png');
