INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (491, 'JR', '', 'Описание Трусы CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Данные трусы имеют очень легкий вес и зауженную посадку, для лучшей мобильности и комфорта. В защите позвоночника используется очень легкий композитный материал ROCKETFRAME. Защита бедер выполнена из пены высокой плотности и вставок из формованного полиэтилена, для максимальной защиты. Внутренняя подкладка DRYFORM, которая эффективно отталкивает влагу. Чтобы подобрать идеальную форму шорты имеют регулируемую посадку и могут удлиняться на 1« (2,5 см) за счет вшитой под поясом молнии. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Особенности: • Уровень – Элитный • Удобство – Зауженное • Защита позвоночника – ROCKETFRAME • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Пена высокой плотности; Формованные вставки PE • Подкладка – DRYFOAM • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден; усилен боковыми вставками в области бедра Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/1fb/400_400_140cd750bba9870f18aada2478b24840a/b55537d8_291a_11e9_8927_e0cb4ee6cf35_ef8c7b90_2b69_11e9_99b1_3085a97fbce9.jpg?158331175140638', null, 'JETSPEED FT1', 2, 493, 9, 'C:\Users\vinto/images/Трусы/CCM_JETSPEED FT1_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (492, 'JR', '', 'Описание Трусы CCM SUPER TACKS AS1, топовая модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется плавающая конструкция с пеной D3O, которая рассеивает энергию удара, эта же пена используется для защиты тазобедренных суставов тела. В области бедра для максимальной защиты используется пена высокой плотности и пластик. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Элитный ✅Удобство – Анатомическое ✅Защита позвоночника – D3O ✅Защита почек – Пластик ✅Защита тазобедренная – JDP; D30 ✅Защита бедра – Пластик; Пена высокой плотности ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5d2/400_400_140cd750bba9870f18aada2478b24840a/649867b1_78a8_11e9_aa99_a4bf0158c9f5_a40c9eec_79be_11e9_aa99_a4bf0158c9f5.jpg?158329958040621', null, 'SUPER TACKS AS1', 2, 494, 9, 'C:\Users\vinto/images/Трусы/CCM_SUPER TACKS AS1_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (494, 'SR', '', 'Описание Трусы BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5f8/400_400_140cd750bba9870f18aada2478b24840a/2432e88a_90db_11e9_aa9a_a4bf0158c9f5_5ab88dde_99de_11e9_aa9a_a4bf0158c9f5.jpg?158329940138396', null, 'SUPREME 2S', 1, 496, 9, 'C:\Users\vinto/images/Трусы/BAUER_SUPREME 2S_SR.png');
