INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (555, 'YTH', '', 'Описание Шлем BAUER Prodigy с маской разработанный специально для детей. Лёгкое застегивание маски. Основа шлема выполнена из высокопрочного пластика с ребрами жесткости для обеспечения стабилизации формы шлема. Подкладка из пены двойной плотности для защиты головы и улучшенного комфорта. Подвижные дуги для ремня не мешают во время игры и не дают шлему слететь с головы. Размер шлема легко регулируется с помощью клипс-замков по бокам шлема. Шлем сертифицирован CSA, HECC, CE. Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/75f/400_400_140cd750bba9870f18aada2478b24840a/5354532d_0bd1_11e6_9fde_001fc6b58537_12f489d0_27ae_11e6_beab_1caff705a9b9.jpg?158330893563353', null, 'PRODIGY YOUTH COMBO', 1, 557, 6, 'C:\Users\vinto/images/Шлем/BAUER_PRODIGY YOUTH COMBO_YTH.png');
