INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (607, 'JR', '', 'Описание Клюшка CCM RIBCOR 63K GRIP, любительская модель линейки RIBCOR 2018 года. Клюшки семейства RIBCOR разработана с целью придать каждому броску больше резкости. Точка прогиба у данной модели смещена в нижнюю часть, а крюк SXX2 имеет прогрессивно нарастающую жесткость от пятки к носку, для более резкого и точного броска. Длина производителя - 49 дюймов Полная длина клюшки - 140 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Нижняя ✅Технология крюка – SXX2 Характеристики Возрастная группа JR Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/133/400_400_140cd750bba9870f18aada2478b24840a/6423838b_7898_11e9_aa99_a4bf0158c9f5_f6b123d1_79c1_11e9_aa99_a4bf0158c9f5.jpg?158329868620033', null, 'RIBCOR 63K GRIP', 2, 609, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_RIBCOR 63K GRIP_JR.png');
