INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (560, 'YTH', '', 'Описание Шлем BAUER RE-AKT 100 YTH детская модель отличается легкой конструкцией и обеспечивает высокий уровень защиты. Ударопрочный корпус состоит из двух частей, смещающихся относительно друг друга, и имеет возможность регулировки плотности посадки без использования дополнительного инструмента за счет регулируемых затылочных упоров Spring-loaded Occipital Lock. Приток воздуха к голове и эффективный отвод тепла обеспечивают вентиляционные отверстия, имеющиеся в корпусе шлема. Внутреннее амортизирующее наполнение выполнено из пены тройной плотности. Специальная система защиты Suspend-Tech позволяет уменьшить вращательную силу при ударах по касательной, гарантируя эффективную защиту. Плавающая накладка, выполненная из пены Poron XRD, при ударе остается неподвижной, в то время как корпус шлема с амортизирующим наполнением смещается относительно головы на небольшой угол, рассеивая энергию удара. Прозрачный элемент, изготовленный из пластика и имеющий прорезиненное покрытие, обеспечивает надежную защиту ушной раковины. Возникновение неприятных запахов в процессе использования шлема предотвращает использование антибактериальной пропитки Microban. В области лба расположена дополнительная, влагопоглощающая прокладка, которая исключает попадание влаги в глаза. Ремешок под подбородком разработан специально для младших игроков и имеет возможность быстро затягиваться, фиксируя шлем на голове игрока. На шлем можно установить маску. Данная модель сертифицирована HECC, CSA и отвечает требованиям безопасности стандарта CE. Маска на шлем полевого игрока BAUER RE-AKT SR обеспечивает надежную защиту, полностью закрывая лицо. Маска изготовлена из ударопрочного титанового сплава, что делает ее на 46% легче традиционных защитных масок. Маска обеспечивает отличную видимость, за счет прямоугольной формы, овального сечения прутьев и оптимально подобранного расстояния между прутьями. Специально подобранный цвет маски не отражает свет прожекторов, делает черную шайбу более видимой, не отвлекает внимание игрока, позволяя полностью сконцентрироваться на движении шайбы. Плавающая, предварительно сформованная чаша для подбородка, выполнена из вспененного материала Poron XRD, обеспечивающего отвод влаги и отлично амортизирующего удары. Маска совместима с большинством сертифицированных хоккейных шлемов, подходит для всех шлемов Bauer. Маска прошла сертификацию CSA, HECC и имеет европейский знак соответствия требованиям безопасности CE. Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/55c/400_400_140cd750bba9870f18aada2478b24840a/53545335_0bd1_11e6_9fde_001fc6b58537_12f489e0_27ae_11e6_beab_1caff705a9b9.jpg?158328923465488', null, 'REAKT 100 YOUTH COMBO', 1, 562, 6, 'C:\Users\vinto/images/Шлем/BAUER_REAKT 100 YOUTH COMBO_YTH.png');
