INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (591, 'YTH', '', 'Описание Клюшка WARRIOR COVERT QRE, топовая модель линейки COVERT 2018 года. Особенности: • EDGE QUICK RELEASE – Клюшки данной серии имеют максимально низкую точку прогиба SUPER LOW KICK для быстрых и резких бросков • EDGE TAPER – Уникальный профиль клюшки обеспечивает один из самых быстрых и точных бросков, благодаря ромбовидной форме сужения клюшки • MINIMUS CARBON 1200 - Высокопрочная, надежная и легкая конструкция из цельнолитого углеродистого волокна. Новая геометрия края клюшки в момент броска, мгновенно и точно передает энергию на крюк, бросок получается более резкий и мощный. • TRUE 1 PHANTOM – Клюшка имеет полностью монолитную конструкцию и отличается хорошей сбалансированностью и прочностью, увеличивает чувство владения клюшкой во время игры, дает вам больше контроля и реакции • PRO CORTEX GRIP – Нескользящее текстурированное мягкое покрытие клюшки- легко удерживать в руках для оптимального контроля Полная длина клюшки - 118 см Характеристики Возрастная группа YTH Линейка COVERT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/40d/400_400_140cd750bba9870f18aada2478b24840a/0dae6858_a9c8_11e8_9962_3085a97fbce9_0dae6859_a9c8_11e8_9962_3085a97fbce9.jpg?158328133322518', null, 'COVERT QRE GRIP TYKE', 3, 593, 14, 'C:\Users\vinto/images/Клюшки игрока/WARRIOR_COVERT QRE GRIP TYKE_YTH.png');
