INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (595, 'INT', '', 'Описание Клюшка BAUER VAPOR S18 X600 LITE GRIPTAC Когда приходит ваш шанс, нужна быстро реагирующая клюшка, на которую вы можете рассчитывать. Vapor X600 Lite спроектирована с использованием новейшей технологии крюков BridgeCore, обеспечивающей дополнительную стабильность, позволяющую вам больше контролировать шайбу и обезоруживать вратарей. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Единая конструкция из 2 частей • Эффективная углеродная конструкция крюка Конструкция/технология изготовления крюка: • Наружная часть: Эффективная углеродная конструкция крюка • Внутренняя часть: Технология BridgeCore Blade • Профиль крюка Pure Shot Форма рукоятки: • Размеры рукоятки Micro Feel II и выгнутые с двух сторон стенки • Конусность рукоятки • Двойное сужение VAPOR Premium Варианты захвата: • Покрытие GRIPTAC Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/004/400_400_140cd750bba9870f18aada2478b24840a/05a60227_c381_11e7_9cca_3085a97fbce9_d5421066_c510_11e7_9ccb_3085a97fbce9.jpg?158331315915372', null, 'VAPOR S18 X600 LITE GRIPTAC', 1, 597, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR S18 X600 LITE GRIPTAC_INT.png');
