INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (630, 'YTH', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 46 дюймов Полная длина клюшки - 129 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme; DURAFLEX(INT-SR) • Технология крюка - ACL; R-LITE; eLASTech(INT-SR) • Конструкция – MONOCOMP Характеристики Возрастная группа YTH Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f2d/400_400_140cd750bba9870f18aada2478b24840a/68b5a7a7_6ae3_11e8_995b_3085a97fbce9_09783697_9bca_11e8_995f_3085a97fbce9.jpg?158329546821452', null, 'NEXUS 2N PRO GRIPTAC', 1, 632, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_NEXUS 2N PRO GRIPTAC_YTH.png');
