INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (651, 'SR', '', 'Описание Клюшка CCM JETSPEED PRO2, профессиональная модель линейки JETSPEED 2019 года. Клюшка имеет гибридную точку прогиба, благодаря этому клюшка подходит, как для резких кистевых, так и для мощных бросков. Данная модель выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует прочность и лучшую стабильность при эксплуатации. Крюк клюшки JS4 BLADE имеет сверхлегкую конструкцию с амортизированный сердечником и усиленной пяткой и носком. Особенности: ✅Уровень – ПРОФЕССИОНАЛЬНЫЙ ✅Точка прогиба – Гибридная ✅Технология трубы – X-FLOW ✅Технология крюка – JS4 BLADE ✅Материал – Углеродное волокно Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/cf8/400_400_140cd750bba9870f18aada2478b24840a/240a02ac_432e_11ea_aa9b_a4bf0158c9f5_026b0091_4746_11ea_aa9b_a4bf0158c9f5.jpg?158329873918995', null, 'JETSPEED PRO2 GRIP', 2, 653, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_JETSPEED PRO2 GRIP_SR.png');
