INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (694, 'INT', '', 'Описание Клюшка CCM JETSPEED FT2, топовая модель линейки JETSPEED 2019 года. Данная клюшка выполнена из углеродного волокна SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данное волокно имеет особое плетение, обеспечивая прочность и легкость клюшки. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Клюшка имеет гибридную точку прогиба, благодаря этому клюшка подходит, как для резких кистевых, так и для мощных бросков. Крюк клюшки имеет амортизированный сердечник JF-80, благодаря этому мы получаем сверхлегкий крюк с усиленными пяткой и носком. Длина клюшки: ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Гибридная ✅Технология трубы – X-FLOW ✅Технология крюка – JF-80 Характеристики Возрастная группа INT Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d93/400_400_140cd750bba9870f18aada2478b24840a/ab0c4910_59c0_11e9_99c5_3085a97fbce9_3ab2f80a_99cd_11e9_aa9a_a4bf0158c9f5.jpg?158327903819026', null, 'JETSPEED FT2 GRIP', 2, 696, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_JETSPEED FT2 GRIP_INT.png');
