INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (706, 'SR', '', 'Описание Клюшка CCM RIBCOR TRIGGER 4 PRO, топовая модель линейки CCM 2019 года. При производстве клюшки используется высокотехнологичное углеродное волокно SIGMATEX 18К, делая клюшку невероятно легкой и прочной. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая конструкция ERGONOMIC SHAFT DESIGN обеспечивает разную форму шафта по всей длине клюшки, придавая максимальное удобство и комфорт во время игры. Клюшка имеет нижнюю точку прогиба с новым ассиметричным тэйпером, который предполагает традиционную вогнутость с внутренней стороны, обеспечивая максимальную загрузку клюшки и наличие выпуклой геометрии с внешней стороны тэйпера для большей точности бросков и контроля шайбы. Новый крюк ADGILITY BLADE имеет мягкую конструкцию для более быстрых и резких бросков. Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология трубы – X-FLOW; ERGONOMIC SHAFT DESIGN ✅Технология крюка – ADGILITY BLADE ✅Материал – SIGMATEX 18К Характеристики Возрастная группа SR Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/496/400_400_140cd750bba9870f18aada2478b24840a/74b81089_11ca_11ea_aa9a_a4bf0158c9f5_d5f141c2_1b1f_11ea_aa9a_a4bf0158c9f5.jpg?158330047018615', null, 'RIBCOR TRIGGER 4 PRO GRIP', 2, 708, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_RIBCOR TRIGGER 4 P