INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (425, 'JR', '', 'Описание Перчатки BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Основное преимущество этих перчатки, то, что они стали легче и более защищенные относительно предыдущей линейки VAPOR. У перчаток клиновидное удобство, которое характеризуется более плотным прилегание в области пальцев и свободным в области манжеты. Ладонь у перчаток выполнена из материала QUATTRO GRIP AX SUEDE , которая дает игроку отличное ощущение клюшки и долговечность. Так же ладонь дополнительно усилена в стратегически важном месте. Особенности: • Уровень – Элитный • Удобство – Клиновидное • Внешний материал – Искусственная кожа; высокопрочная нейлоновая сетка • Защита тыльной стороны – Пена высокой плотности • Защита пальцев – Пена высокой плотности • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 3-х частей) • Ладонь – QUATTRO GRIP AX SUEDE • Подкладка – 37,5®; SANITIZED® Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d20/400_400_140cd750bba9870f18aada2478b24840a/5523c45d_7a47_11e8_995d_3085a97fbce9_8b1b70e2_7a5f_11e8_995d_3085a97fbce9.jpg?158331166890178', null, 'VAPOR 1X LITE', 1, 427, 12, 'C:\Users\vinto/images/Перчатки/BAUER_VAPOR 1X LITE_JR.png');
