INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (6, 'YTH', '', 'Описание Коньки BAUER NEXUS N6000 SR - это комфортная игра под вашим контролем. Легкие ботинки, комфортные подложки для лодыжек из пены и стелька из формованной пены – вы будете в превосходной форме на протяжении всей игры. Верхняя часть: * Конструкция Quarter Package - Легкий сетчатый материал * Материал подкладки - Ворсовый нейлон * Защита лодыжки - Комфортные подкладки на лодыжках из пены * Конструкция языка - Удобный войлочный язычок определенной формы с метатарзальной защитой * Стелька - Материал EVA, формованный для комфорта Нижняя часть: * Подошва - TPR * Стакан - TUUK LIGHTSPEED Pro * Лезвие - Лезвия из нержавеющей стали TUUK Super Характеристики Возрастная группа YTH Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a82/400_400_140cd750bba9870f18aada2478b24840a/2d6faeb9_0d35_11e7_9c76_3085a97fbce9_06524fd7_62d8_11e7_9c9c_3085a97fbce9.jpg?158329522350609', null, 'NEXUS N6000', 1, 8, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_NEXUS N6000_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (82, 'SR', '', 'Описание Баул вратаря WARRIOR RITUAL GOALIE ROLLER BAG, модель 2019 года. ✅Основа 840 ден с полиуретановыми вставками 420 ден ✅Карман для двух клюшек ✅Усиленные прорезиновые углы ✅Карман для коньков ✅U-образная молния сверху ✅Внутренний карман на молнии ✅Широкие ручки-ремни ✅Пластмассовые вентиляционные вставки ✅Окошко для ID игрока ✅Усиленное основание на колесах ✅Размер – 105*60*60 см Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/26c/400_400_140cd750bba9870f18aada2478b24840a/fb05f696_3917_11ea_aa9a_a4bf0158c9f5_74981d02_3fab_11ea_aa9b_a4bf0158c9f5.jpg?158329302647121', null, 'RITUAL GOALIE ROLLER BAG 44', 3, 84, 27, 'C:\Users\vinto/images/Сумки вратаря/WARRIOR_RITUAL GOALIE ROLLER BAG 44_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (434, 'SR', '', 'Описание Перчатки BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки и излом в области фаланговых суставов для максимальной подвижности и удобства. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала AX SUEDE QUATTRO, обеспечивает вам комфорт, а благодаря эластичным вставкам COMFORT STRETCH FIT безупречно передает тактильные ощущения. Внутренняя подкладка THERMO MAX и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Профессиональный • Удобство – Анатомическое • Внешний материал – Плетеная сетка CABLE MESH; Нейлон • Защита тыльной стороны – Пена двойной плотности; вставки из пластика • Защита пальцев – Пена средней плотности; вставки из пластика • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 2-х частей) • Ладонь – AX SUEDE QUATTRO • Подкладка – THERMO MAX; HYPERSENSE Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/06d/400_400_140cd750bba9870f18aada2478b24840a/21c03c15_7638_11e9_aa99_a4bf0158c9f5_d918cc8c_87ff_11ea_aa9e_a4bf0158c9f5.jpg?158800346080381', null, 'SUPREME 2S', 1, 436, 12, 'C:\Users\vinto/images/Перчатки/BAUER_SUPREME 2S_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (435, 'SR', '', 'Описание Перчатки WARRIOR COVERT QRE, топовая модель линейки перчаток COVERT 2018 года. Особенности: • QUICK RELEASE – Идеальный баланс классического дизайна и современных знаний об анатомии позволяет улучшить контроль шайбы и хорошо защитить руки. Перчатки модели Covert идеально сочетают в себе удобство и надежность • AXYFLEX CUFF – Манжета axyflex позволяет запястью оставаться подвижным при обработке и броске, обеспечивая полную защиту от ударов • AXYFLEX THUMB – Усиливает мобильность и манёвренность • COVERT TAPER FIT – Повторяет естественное очертание запястья и рук, чтобы максимально увеличить подвижность и защиту • BUTTER SOFT FEEL – Лайкровые вставки в пальцах, технология AXI-Flex сгибаемый большой палец и анатомическая верхняя тянущаяся поверхность • PRO PHANTON FOAM – Пена PHANTOM и сегментированные пластиковые вставки, обеспечивают наилучшую защиту от сильных ударов • SMART PALM+ - Обеспечивает сочетание прочности и комфорта в стратегически важных местах • GATORSKIN PALM OVERLAY – Абразивный материал, обеспечивающий лучшую изностойкость • WARTECH FNC LINER – Антибактериальная подкладка обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка COVERT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5ca/400_400_140cd750bba9870f18aada2478b24840a/45ebf08e_8a4b_11e8_995e_3085a97fbce9_10d46353_c7cd_11e8_9979_3085a97fbce9.jpg?158330602068533', null, 'COVERT QRE', 3, 437, 12, 'C:\Users\vinto/images/Перчатки/WARRIOR_COVERT QRE_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (439, 'SR', '', 'Описание Перчатки CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Благодаря внешнему покрытию перчаток из высококачественного полиэстера и искусственной кожи перчатки очень прочны и износостойки. В перчатки используется гибкая манжета для лучшей подвижности руки. Тыльная сторона перчатки для максимальной защиты, выполнена из трех разных технологий. В первом слое использована пена премиум класса ZOTEFOAM, которая не впитывает влагу и сохраняет перчатки легкий вес. Следующий слой выполнен из пены D3O, которая обладает высокой степенью поглощения удара. И третий слой это пластиковые вставки PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала PRO PALM CLARINO, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал пенополиуретан PU выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Внешний материал – Искусственная кожа; высококачественный полиэстер • Защита тыльной стороны – D3O; ZOTEFOAM; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – PRO PALM CLARINO • Подкладка – Полиуретан PU;POLYGIENE Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/541/400_400_140cd750bba9870f18aada2478b24840a/43db7ad6_88f4_11e8_995e_3085a97fbce9_43db7ad7_88f4_11e8_995e_3085a97fbce9.jpg?158330839884058', null, 'JETSPEED FT1', 2, 441, 12, 'C:\Users\vinto/images/Перчатки/CCM_JETSPEED FT1_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (10, 'YTH', '', 'Описание Коньки BAUER SUPREME S27 YTH, полупрофессиональная детская модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Внешний материал этого конька изготовлен из высокопрочного полиуретана 3-D TRUEFORM TECH PU. Внутренняя подкладка сделана из водоотталкивающей микрофибры. Язычок выполнен из войлока плотность 30 унции. Подошва изготовлена из термопластичной резины. Особенности: • Уровень – Полупрофессиональный • Внешний материал – 3-D TRUEFORM TECH PU • Внутренний материал – MICROFIBER • Язык – Войлок плотностью 30 унций • Термоформуемость – Частичная формовка в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED PRO II • Лезвие – TUUK S.STAINLESS Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3b5/400_400_140cd750bba9870f18aada2478b24840a/d244771b_de6b_11e8_9987_3085a97fbce9_d244771c_de6b_11e8_9987_3085a97fbce9.jpg?158328684257281', null, 'SUPREME S27', 1, 12, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S27_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (12, 'JR', '', 'Описание Коньки BAUER SUPREME S25, любительская модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Внешний материал этого конька изготовлен полиуретана 3-D TRUEFORM TECH PU. Двухсоставной язычок выполнен из войлока плотность 30 унции. Подошва изготовлена из термопластичной резины. Особенности: • Уровень – Любительский • Внешний материал – 3-D TRUEFORM TECH PU • Внутренний материал – MICROFIBER • Язык – Войлок плотностью 30 унций • Стелька – FORM-FIT EVA • Термоформуемость – Частичная формовка в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED PRO II • Лезвие – TUUK STAINLESS Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/de3/400_400_140cd750bba9870f18aada2478b24840a/f31b4276_7a2f_11e8_995d_3085a97fbce9_c4137ac7_6440_11ea_aa9b_a4bf0158c9f5.jpg?158403018257576', null, 'SUPREME S25', 1, 14, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S25_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (14, 'JR', '', 'Описание Коньки BAUER SUPREME S160, полупрофессиональная модель в новой линейке SUPREME 2016 года. Ботинок коньков имеет анатомическую форму и облегает ногу, образуя удобную и естественную посадку. Внешнее покрытие выполнено из материала 3D TrueForm, армированного слоем Tech PU, что обеспечивает повышенную жесткость ботинка и оптимальную устойчивость. Данный материал термоформируемый и после процедуры термоформовки создает персонализированную посадку. Наполнение в области голеностопного сустава выполнено из легкой пены с эффектом памяти и быстро принимает удобную, анатомически правильную форму. Внешняя, усиленная пяточная чаша обеспечивает дополнительную защиту заднего отдела стопы. Внутренний войлочный язычок имеет анатомическую конструкцию, состоит из двух частей, толщиной 40 oz, с высокопрочной защитой. Внутреннее наполнение язычка из пены высокой плотности защищает подъем стопы и переднюю часть голеностопного сустава от передавливания шнуровкой и ударов шайбой, клюшкой или коньком. Внутренняя стелька Form-Fit EVAспособствует правильному положению стопы внутри ботинка. Подошва производится из термопластичной резины. Стакан Tuuk LightSpeed Edge имеет систему быстрой замены лезвий Trigger System. Лезвия Tuuk LightSpeed Super. Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/81b/400_400_140cd750bba9870f18aada2478b24840a/a9cd6673_4fd6_11e6_9c01_3085a97fbce9_10e9ab47_53c8_11e6_9c01_3085a97fbce9.jpg?158327708253437', null, 'SUPREME S160', 1, 16, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S160_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (17, 'YTH', '', 'Описание Коньки BAUER SUPREME 1S Yth - детская модель, новинка в линейке SUPREME 2016. Ботинок коньков обладает комфортной, анатомической посадкой. После термоформовки ботинки имеют комфортную посадку. Накладки Total Edge Comfort, расположенные по верхней кромке ботинок, создают дополнительный комфорт, исключая натирание мягких тканей ноги при динамичном движении. Суппорт голеностопа, выполненный композитного материала Curv Composite, обеспечивает поддержку голеностопного сустава, повышает жесткость ботинка. Цельная конструкция системы шнуровки Stability Lacing System позволяет сильнее прижать стопу в нижней части ботинка, снизить его вес и увеличить прочность. Пена, обладающая эффектом памяти и используемая как внутреннее наполнение в области лодыжки, обеспечивает плотную фиксацию ноги внутри ботинка, что способствует более эффективной передачи энергии при движении по льду. Внутренний язычок надежно защищает переднюю часть голеностопа, имеет гибридное войлочное покрытие с плотностью 52 oz и 40 oz. Гидрофобный, сетчатый материал, используемый при изготовлении внутренней подкладки, за счет применения технологии 37.5 способствует быстрому испарению влаги, используя для этого тепло тела спортсмена. Композитная подошва с вентиляционными отверстиями гарантирует максимальную отдачу при каждом шаге. В коньках установлены стаканы Tuuk LightSpeed Pro. Коньки снабжены лезвиями из нержавеющей стали Tuuk LightSpeed LS1. Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/616/400_400_140cd750bba9870f18aada2478b24840a/17ac9b01_2ad4_11e6_beb1_8807972027df_9d0e0e50_391e_11e6_bec3_dba2b096c3df.jpg?158327721451834', null, 'SUPREME 1S', 1, 19, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 1S_YTH.png');
