INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (449, 'YTH', '', 'Описание Трусы BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Трусы предназначены для детей начинающего уровня. Для защиты позвоночника используется пена высокой плотности. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности. Защитные щитки, закрывающие бедра имеют цельную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. Внутренняя подкладка HYDROPHOBIC поглощает влагу и способствует быстрому высыханию. Трусы имеют классическую посадку. Особенности: ✅Уровень – Любительский ✅Удобство – Классическое ✅Защита позвоночника – Пена высокой плотности ✅Защита тазобедренная – Пена средней плотности; пластиковые вставки ✅Подкладка – HYDROPHOBIC ✅Регулировка – Отсутствует ✅Чехол – Плотность 400 ден Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/678/400_400_140cd750bba9870f18aada2478b24840a/c0d61922_9812_11e9_aa9a_a4bf0158c9f5_c7ea47bc_99dc_11e9_aa9a_a4bf0158c9f5.jpg?158328323037725', null, 'NSX', 1, 451, 9, 'C:\Users\vinto/images/Трусы/BAUER_NSX_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (452, 'SR', '', 'Описание Трусы BAUER NEXUS N7000 модель начального уровня, отличается хорошим уровнем защиты и имеет удобную, традиционную для этой линейки посадку. При пошиве чехла этой модели используется стойкий к истиранию нейлон плотностью 400 Den. Бедра усилены дополнительными накладками, увеличивающими износостойкость чехла. Эластичные клинья, вшиваемые в чехол, позволяют игроку свободно двигаться и сгибаться. Пена высокой плотности и вкладыш из ударопрочного полиэтилена используются при изготовлении защитного щитка, закрывающего позвоночник. Элемент защиты копчика имеет сегментированное, гибкое строение и выполнен из пены средней плотности и пластиковых вставок. Область тазобедренных суставов защищается куполообразными чашами, которые благодаря своей анатомической конструкции плотно прилегают к телу, создавая эффективную защиту. Чаши выполнены из формованного полиэтилена и выстланы пеной Comfort. Защитные щитки, закрывающие бедра, имеет двухсоставную конструкцию, что обеспечивает максимальный охват бедра и надежную защиту. Легкая пена средней плотности, используемая в набедренных щитках, гасит силу удара, надежно защищая бедра. Завышенная, формованная защита области почек имеет наполнение из пены средней плотности, которая усилена пластиковыми вставками. Гидрофобная, сетчатая подкладка способствует быстрому высыханию защитного снаряжения. На внутренней стороне штанин вшиты дополнительные молнии, которые увеличивают ширину штанин. Трусы фиксируются с помощью шнуровки и нейлонового пояса с прочным карабином. Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/83f/400_400_140cd750bba9870f18aada2478b24840a/1446b723_5f2c_11e6_9c06_3085a97fbce9_1446b724_5f2c_11e6_9c06_3085a97fbce9.jpg?158330724135652', null, 'NEXUS N7000', 1, 454, 9, 'C:\Users\vinto/images/Трусы/BAUER_NEXUS N7000_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (800, 'SR', '', 'Описание Защита горла BAUER NG CORE NLP8 Bib SR Основные особенности: ✅Материал - Усиленный полиэстер для защиты от порезов ✅Технология THERMO MAX+ защищает от неприятных запахов и отводит влагу ✅Воротник служит для дополнительной защиты в области груди и ключицы и не дает защите горла крутиться ✅Размер - 34-43 см ✅Подходит на возраст примерно от 13 лет ✅Сертифицирована Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/143/400_400_140cd750bba9870f18aada2478b24840a/d82c4166_0bd0_11e6_9fde_001fc6b58537_d2cc5d33_3abc_11e6_bec6_d5a05555e1dc.jpg?158327680845649', null, 'NG CORE NLP8 BIB', 1, 802, 11, 'C:\Users\vinto/images/Защита шеи/BAUER_NG CORE NLP8 BIB_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (709, 'INT', '', 'Описание Клюшка BAUER SUPREME 2S PRO, топовая клюшка в линейки SUPREME. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX , которая увеличивает прочность и долговечность. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. В крюке данной модели содержится гель RENEWCORE, который заполняет микротрещины и повышает прочность и долговечность клюшки. При производстве крюка так же используется технология ADVANCED CARBON LAYERING, обеспечивая более высокую плотность между слоями углеродного волокна, в результате чего уменьшается количество смолы, пропитывающей углеродное волокно, что приводит к снижению веса клюшки и лучшему сопротивлению ударным нагрузкам. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Элитный • Точка прогиба – Верхняя • Технология броска – MPK • Технология трубы – TeXtreme; DURAFLEX • Технология крюка - TeXtreme; ACL; RENEWCORE(INT-SR); eLASTech(INT-SR) • Конструкция – MONOCOMP • Геометрия шафта – Квадратная Характеристики Возрастная группа INT Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/838/400_400_140cd750bba9870f18aada2478b24840a/2ba65fa0_d827_11e8_9984_3085a97fbce9_2ba65fa5_d827_11e8_9984_3085a97fbce9.jpg?158331153120272', null, 'SUPREME 2S PRO GRIPTAC', 1, 711, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME 2S PRO GRIPTAC_INT.png');
