INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (713, 'INT', '', 'Описание Клюшка BAUER VAPOR FLYLITE, топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE! Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme; DURAFLEX; ACL • Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE • Конструкция – MONOCOMP • Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/86a/400_400_140cd750bba9870f18aada2478b24840a/60ee217a_8ce4_11e9_aa99_a4bf0158c9f5_5dd016f8_c9dd_11e9_aa9a_a4bf0158c9f5.jpg?158331029920156', null, 'VAPOR FLYLITE GRIP', 1, 715, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR FLYLITE GRIP_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (460, 'SR', '', 'Описание Трусы BAUER VAPOR X800 LITE, полупрофессиональная модель линейки VAPOR 2018 года. Оптимальный выбор для любителей и детей. Отличное сочетание защитных функций и удобства. Внешний материал трусов изготовлен из прочного нейлона плотностью 400 ден. Зоны максимального износа дополнительно усилены нейлоном большей плотностью. Особенности: • Уровень – Полупрофессиональный • Удобство – Клиновидное • Защита позвоночника – Пена HYPERLITE; FREE FLEX • Защита почек – MD FOAM (пена средней плотности) • Защита тазобедренная – Пена; Пластиковые вставки • Защита бедра – Пена; пластиковые вставки • Подкладка – HYDROPHOBIC; SANITIZED® • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ea7/400_400_140cd750bba9870f18aada2478b24840a/32ab0c55_8b16_11e8_995e_3085a97fbce9_32ab0c56_8b16_11e8_995e_3085a97fbce9.jpg?158329973642545', null, 'VAPOR X800 LITE', 1, 462, 9, 'C:\Users\vinto/images/Трусы/BAUER_VAPOR X800 LITE_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (711, 'SR', '', 'Описание Клюшка WARRIOR ALPHA DX, топовая модель линейки ALPHA 2019 года. Особенности: ✅ SABER TAPER – Уникальная конструкция клиновидного сужения создает жесткую к скручиванию зону прогиба клюшки, что делает крюк более стабильным для большой точности удара. Улучшенная конструкция легко задействует прогиб и обеспечивает быстрый высокоточный релиз. ✅ MINIMUS CARBON 1400 – Карбоновая конструкция с плоским плетением, благодаря чему клюшка стала более надежной и легкой. Кроме того, этот материал стал более динамичным и эластичным. ✅ TRUE 1 PHANTOM – Улучшенный баланс и отклик заметно повышают чувствительность и контроль шайбы. Самая легкая и мощная цельнолитая клюшка TRUE 1 ✅ FUELCORE – Легкий полимерный сердечник крюка усиливает ощущение шайбы и делает броски сильнее. Более прочные и жесткие материалы увеличивают долговечность крюка. ✅ ERGO SHAFT SHAPE – Эргономичная форма шафта клюшки удобно и надежно ложится в руки, повышая чувствительность, чтобы вы могли легко совершать удары, финты и убирать шайбу под себя. ✅ APEX GRIP TEXTURE – Текстурированное нескользящее покрытие клюшки обеспечивает максимальный контроль и ощущение. Полная длина клюшки - 165 см Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/fd0/400_400_140cd750bba9870f18aada2478b24840a/3ea0dcf1_af72_11e9_aa9a_a4bf0158c9f5_596d6ee7_8281_11ea_aa9d_a4bf0158c9f5.jpg?158739353918813', null, 'ALPHA DX GRIP', 3, 713, 14, 'C:\Users\vinto/images/Клюшки игрока/WARRIOR_ALPHA DX GRIP_SR.png');
