INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (470, 'SR', '', 'Описание Трусы BAUER SUPREME S27, любительская модель линейки SUPREME 2019 года. Трусы предназначены для игроков-любителей. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. Особенности: ✅Уровень – Любительский ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Подкладка – HYDROPHOBIC ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/490/400_400_140cd750bba9870f18aada2478b24840a/8bd16e14_9812_11e9_aa9a_a4bf0158c9f5_77babeda_99de_11e9_aa9a_a4bf0158c9f5.jpg?158331161540985', null, 'SUPREME S27', 1, 472, 9, 'C:\Users\vinto/images/Трусы/BAUER_SUPREME S27_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (35, 'SR', '', 'Описание Коньки BAUER SUPREME S170, полупрофессиональная модель в новой линейке SUPREME 2016 года. При изготовлении внешнего покрытия ботинка используется легкий материал 3D-lasted Tech Mesh, который обеспечивает высокую прочность, отличную жесткость и необходимую поддержку. Термоформируемый верх позволяет подобрать комфортную, анатомически правильную посадку. Набивка вокруг голеностопного сустава сделана из легкой пены с эффектом памяти, которая аккуратно облегает голеностоп, сохраняя форму и устраняя пустое пространство вокруг суставов. Внешняя пяточная чаша анатомической формы фиксирует пятку и обеспечивает оптимальную поддержку. Внутренний войлочный язычок Form-Fit, плотностью 48 oz, состоит из двух частей. Внутренняя набивка язычка из толстого слоя пены высокой плотности обеспечивает необходимую защиту передней части голеностопного сустава. Внутренняя подкладка выполнена из гидрофобной микрофибры, которая быстро отводит излишки влаги от стопы с последующим быстрым ее испарением, обеспечивая ногам комфортное состояние. Модернизированная стелька Form-Fit+ имеет противоскользящее покрытие, препятствуя проскальзыванию стопы внутри ботинка и способствуя эффективной передаче энергии при движении. Подошва Pro TPU обладает повышенной жесткостью. На коньках стоят стаканы Tuuk LightSpeed Edge, которые снабжены системой быстрой смены лезвий Trigger System. В стаканах используются лезвия из нержавеющей стали Tuuk LightSpeed LS.1. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/77c/400_400_140cd750bba9870f18aada2478b24840a/17ac9b04_2ad4_11e6_beb1_8807972027df_9d0e0e5e_391e_11e6_bec3_dba2b096c3df.jpg?158328570050813', null, 'SUPREME S170', 1, 37, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S170_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (36, 'SR', '', 'Описание Коньки BAUER VAPOR S17 X600 - это полупрофессиональная модель в линейке VAPOR 2017 года. Единственное, что увидят ваши соперники в тот момент, когда вы промчитесь рядом в своих новых коньках X600, — это как нечто пронеслось мимо. Волокнистый композитный верх с рифлением X-rib, а также 1-компонентная интегрированная пятка и основа лодыжки предназначены для скорости. Буквально. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Волокнистый композитный верх с рифлением X-rib • Материал подкладки - Гидрофобная микрофибра • Защита лодыжки - Подушки для лодыжки из анатомического пеноматериала. • Конструкция языка - Анатомической формы, вес 40 унций, из двух частей, с пенным наполнителем высокой плотности, оснащен метатарзальной защитой • Стелька - FORM-FIT • Термоформуемость - Термоформованная передняя часть • Поддержка пятки - 1-компонентная интегрированная основа пяты/лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Прозрачный интегрированный TPU • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - Лезвия из нержавеющей стали TUUK Super Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e9f/400_400_140cd750bba9870f18aada2478b24840a/2d6faea9_0d35_11e7_9c76_3085a97fbce9_59d21253_581a_11e8_9958_3085a97fbce9.jpg?158329255656052', null, 'VAPOR S17 X600', 1, 38, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 X600_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (474, 'SR', '', 'Описание Трусы CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Трусы данной модели имеют чехол, выполненный из текстурированного полиэстера плотностью 400 ден. Защитные функции бедра выполнены из вспененного полиэтилена. Так же вставки из вспененного полиэтилена обеспечивают высокий уровень защиты в области почек и тазобедренного сустава. Особенности: • Уровень – Полупрофессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – Влагоотталкивающий полиэстер • Регулировка – нет • Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ce3/400_400_140cd750bba9870f18aada2478b24840a/a1430be0_d5cd_11e8_9984_3085a97fbce9_4d4acd69_d600_11e8_9984_3085a97fbce9.jpg?158329997136891', null, 'JETSPEED FT370', 2, 476, 9, 'C:\Users\vinto/images/Трусы/CCM_JETSPEED FT370_SR.png');
