INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (478, 'SR', '', 'Описание Трусы CCM SUPER TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется формованный полиэтилен. В области бедра и почек для максимальной защиты используется пластик. Для защиты тазобедренных суставов используется конструкция JDP, которая рассеивает удар. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – JDP ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/10e/400_400_140cd750bba9870f18aada2478b24840a/59c96349_78a4_11e9_aa99_a4bf0158c9f5_f832cb04_79be_11e9_aa99_a4bf0158c9f5.jpg?158329415039961', null, 'TACKS 9060', 2, 480, 9, 'C:\Users\vinto/images/Трусы/CCM_TACKS 9060_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (47, 'SR', '', 'Описание Коньки BAUER VAPOR X2.7, полупрофессиональная модель линейки VAPOR 2019 года. Конек данной модели выполнен по технологии ONE PIECE 3D POLYCARB QUARTER, благодаря чему ботинок лучше сидит в области лодыжки, а также отличается небольшим весом, обладает повышенным удобством и гибкостью. Внутренняя подкладка из мягкой микрофибры дополнительно повышает комфорт внутри конька. Коньки оснащены войлочным язычком плотностью 40 унций с защитной вставкой из пены высокой плотности, в области плюсневой кости, которая помогает достичь максимального комфорта, а также хороших защитных свойств. Лезвия данной модели выполнены из высококачественной и прочной нержавеющей стали. Особенности: ✅Уровень – Любительский ✅Внешний материал – ONE PIECE 3D POLYCARB QUARTER ✅Внутренний материал – MICROFIBER ✅Язык – Войлок плотностью 40 унций ✅Стелька – FORM FIT ✅Термоформуемость – Частичная формовка в области лодыжки ✅Подошва – TPU ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK STAINLESS Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/393/400_400_140cd750bba9870f18aada2478b24840a/7633cd1a_980e_11e9_aa9a_a4bf0158c9f5_b7a2a25a_99cf_11e9_aa9a_a4bf0158c9f5.jpg?158328520154410', null, 'VAPOR X2.7', 1, 49, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR X2.7_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (52, 'JR', '', 'Описание Коньки BAUER VAPOR S17 X900 - это профессиональная модель в линейке VAPOR 2017 года. Какую скорость можно развить на коньках? Примерьте X900 и проверьте сами. Композитный ботинок с эффектом 3D Curv® и система LOCK-FIT+ обеспечивают лучшую посадку, позволяя лучше чувствовать лед. Наша самая жесткая подошва, изготовленная из композитного материала, обеспечивает стабильность на предельных скоростях на протяжении всей игры. Можете ли вы справиться со скоростью? ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитная верхняя часть 3D Curv® с дизайном X-rib • Материал подкладки - Подкладка LOCK FIT+ • Защита лодыжки - Легкие подушки для лодыжек из пены анатомической формы • Конструкция языка - Язычок FLEX-LOCK + • Стелька - Стелька LOCK FIT+ • Термоформуемость - Верхняя часть анатомической формы • Поддержка пятки - Анатомическая поддержка для пятки и лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Легкая, полностью композитная • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - Нержавеющая сталь TUUK LS3 (размеры 4-12), нержавеющая сталь TUUK LS2 (размеры 3-3,5) Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a71/400_400_140cd750bba9870f18aada2478b24840a/2d6faead_0d35_11e7_9c76_3085a97fbce9_59d21280_581a_11e8_9958_3085a97fbce9.jpg?158330197364524', null, 'VAPOR S17 X900', 1, 54, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 X900_JR.png');
