INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (480, 'JR', '', 'Описание Трусы WARRIOR COVERT QRE, топовая модель новой линейки ALPHA 2019 года. • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • ADJUSTABLE LEG EXTENSION – Универсальное решение для растущих игроков. Застежка молния обеспечивает возможность удлинить трусы на 1 дюйм • AXYFLEX KIDNEY AND SPINE PROTECTION – Плавающий внутренний поясной ремень обеспечивает комфорт и подвижность, а также защищает позвоночник и почки • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Характеристики Возрастная группа JR Линейка COVERT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ecd/400_400_140cd750bba9870f18aada2478b24840a/22f49453_ee6d_11e9_aa9a_a4bf0158c9f5_033fed14_efe3_11e9_aa9a_a4bf0158c9f5.jpg?158331177844783', null, 'COVERT QRE', 3, 482, 9, 'C:\Users\vinto/images/Трусы/WARRIOR_COVERT QRE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (54, 'SR', '', 'Описание Коньки BAUER VAPOR S17 X800 - это профессиональная модель в линейке VAPOR 2017 года. Станьте мобильнее в любом направлении благодаря оптимальной гибкости коньков X800. Композитный ботинок с эффектом 3D Curv®, рифлением X-rib и системой LOCK-FIT поднимают качество посадки и комфорта до уровня чемпионатов. Мчитесь за шайбой и опережайте противников. Больше скорости, больше славы. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитная верхняя часть 3D Curv® с дизайном X-rib • Материал подкладки - Подкладка LOCK FIT • Защита лодыжки - Легкие подушки для лодыжки из пены двойной плотности • Конструкция языка - Язычок FLEX-LOCK • Стелька - Стелька LOCK FIT • Термоформуемость - Верхняя часть анатомической формы • Поддержка пятки - Анатомическая поддержка для пятки и лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Pro TPU с графическим дизайном • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - TUUK LS2 из нержавеющей стали Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c94/400_400_140cd750bba9870f18aada2478b24840a/2d6faea7_0d35_11e7_9c76_3085a97fbce9_59d21277_581a_11e8_9958_3085a97fbce9.jpg?158328211663062', null, 'VAPOR S17 X800', 1, 56, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 X800_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (55, 'SR', '', '', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/365/400_400_140cd750bba9870f18aada2478b24840a/a360f977_0bd1_11e6_9fde_001fc6b58537_b74bce1a_3921_11e6_bec3_dba2b096c3df.jpg?158329792762680', null, 'VAPOR X900', 1, 57, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR X900_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (56, 'JR', '', 'Описание Коньки BAUER SUPREME 1S, топовая модель в линейке SUPREME 2016 года. Конёк точно повторяет анатомические изгибы стопы и обеспечивает меньшее негативное пространство внутри, в результате конёк имеет плотную, комфортную посадку еще до процесса термоформовки. Ботинок производится из новейшего, композитного материала 3D-lasted Titanium Curv, который значительно снижает вес коньков, обеспечивает повышенную прочность и жесткость. Данный материал пластичен и после процедуры термоформовки ботинки получают комфортную, плотную, индивидуальную посадку. Коньки получили новый суппорт голеностопа Curv Composite, который способствует снижению веса, повышает жесткость и плотно охватывает ногу в области голеностопного сустава. Задник ботинка, защищающий ахиллово сухожилие, выполнен по технологии C-Flex и имеет вставки с различным уровнем жесткости, которые позволяют получить три разных уровня жесткости – отсутствие вставки обеспечивает максимальную гибкость и наибольший диапазон движения, вставка Mid обеспечивает традиционную жесткость и более жесткая вставка Stiff обеспечивает дополнительное сопротивление. Ботинок имеет встроенную, стабилизирующую систему шнуровки Stability Lacing System, ее цельная конструкция позволяет плотнее прижать стопу в нижней части ботинка, уменьшает вес и повышает прочность. Люверсы имеют стабилизирующую, Т-образную форму, что исключает необходимость перешнуровывания ботинка во время игры и позволяет сохранить усилие затяжки. Накладки в верхней части ботинок Total Edge Comfort исключают травмирование мягких тканей ноги верхним краем ботинка при агрессивном катании, обеспечивая необходимый комфорт. Обивка в области голеностопного сустава выполнена из легкой пены с эффектом памяти и позволяет убрать свободное пространство внутри ботинка, плотно фиксируя стопу. Язычок так же выполнен с применением технологии C-Flex и имеет вставки с различным уровнем жесткости, которые выполняют защитную функцию и позволяют увеличить диапазон движения и мощность шага. Внутренняя стелька Speed Plate выполнена из пластичного материала и после термоформовки повторяет контур стопы, придает шагу пружинистость, способствует повышению передачи энергии при каждом шаге. Гидрофобный материал с технологией 37.5 используется в качестве внутренней подкладки. Данный материал позволяет использовать тепло тела для быстрого испарения влаги, обеспечивая быстрый отвод тепла и влаги от ноги и создавая исключительный уровень комфорта. На коньках стоят стаканы Tuuk LightSpeed Edge, которые выше на 3 мм, чем традиционные стаканы, что позволило увеличить угол атаки и уменьшить радиус разворота и, как результат, улучшить маневренность игрока. В данной модели установлены профессиональные лезвия из нержавеющей стали Tuuk LightSpeed LS4, имеющие улучшенную отделку поверхности, что обеспечивает более острый край и лезвия лучше держат заточку. Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5c7/400_400_140cd750bba9870f18aada2478b24840a/978f848f_0bd1_11e6_9fde_001fc6b58537_763cc524_391e_11e6_bec3_dba2b096c3df.jpg?158330068551206', null, 'SUPREME 1S', 1, 58, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 1S_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (57, 'JR', '', 'Описание Коньки BAUER VAPOR S17 1X - это топовая модель в линейке VAPOR 2017 года. БОЛЬШЕ КОНТАКТА. БОЛЬШЕ СКОРОСТИ. Получите беспрецедентную посадку и маневренность, которые позволят вам реализовать в игре каждый грамм вложенной вами энергии. 1. Язычок FLEX-LOCK PRO: Термоформованные вставки фиксируют посадку, сохраняя при этом гибкость переднего контура и обеспечивая защиту, удобство и маневренность. 2. Подкладка LOCK-FIT PRO: Обеспечивает сухость и фиксацию ваших ног для максимальной ловкости и ускорения. 3. Шнуровка LOCK-FIT PRO: Благодаря дополнительной фиксации шнуровкой и более мягкому гибкому материалу, коньки 1X с интегрированными вставками надежно зафиксируют ваши ноги и помогут развить максимальную скорость. АСИММЕТРИЧНАЯ ПОСАДКА Производители трансформировали квадратные вставки в соответствии с естественным положением лодыжки, обеспечив вас дополнительной медиальной поддержкой и максимальной гибкостью. УСКОРЬТЕ ИГРУ Производительность и качество, которым вы доверяете. Разработанная лабораторией OD1N, ультралегкая 3D Curv® композитная конструкция X-Rib, усиливает фиксацию пятки и гибкость лодыжки, увеличивая скорость и улучшая маневренность в поворотах. ИГРА НА ОСТРИЕ Запатентованная компанией Bauer система крепления LIGHTSPEED EDGE с курком, позволяет легко и быстро менять затупившееся лезвие. НАКАЛИТЕ ИГРУ Оставьте своего соперника позади благодаря более чистому, надежному и острому лезвию LIGHTSPEED 4 EDGE. Дополнительные 3 мм увеличивают рост и расширяют ваши возможности при нападении. ОПТИМАЛЬНЫЙ БАЛАНС И СТАБИЛЬНОСТЬ SPEED PLATE™ принимает форму стопы, обеспечивая оптимальный баланс и стабильность. Вдобавок увеличение площади контакта между ногой и лезвием способствует улучшению контроля за движениями, необходимого для ускоренного разгона. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитный ботинок с эффектом 3D Curv®, дизайномX-rib и комфортная подкладка для лодыжки • Материал подкладки - Вкладка LOCK-FIT PRO • Защита лодыжки - Легкие, запоминающие форму тела подушки для лодыжек • Конструкция языка Язычок FLEX-LOCK PRO • Стелька - BAUER SPEED PLATE™ • Термоформуемость - Верхняя часть и стелька анатомической формы • Поддержка пятки - Curv® композитная поддержка для пятки и лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Полностью карбоновая композитная подошва с вентиляцией • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - TUUK LS4 из нержавеющей стали Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/4fc/400_400_140cd750bba9870f18aada2478b24840a/2d6faea5_0d35_11e7_9c76_3085a97fbce9_0e8f1675_7bfc_11e7_9ca5_3085a97fbce9.jpg?158331209259662', null, 'VAPOR S17 1X', 1, 59, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 1X_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (58, 'JR', '', 'Описание Коньки BAUER SUPREME 1S LE, лимитированая модель в линейке SUPREME 2016 года. Конёк точно повторяет анатомические изгибы стопы и обеспечивает меньшее негативное пространство внутри, в результате конёк имеет плотную, комфортную посадку еще до процесса термоформовки. Ботинок производится из новейшего, композитного материала 3D-lasted Titanium Curv, который значительно снижает вес коньков, обеспечивает повышенную прочность и жесткость. Данный материал пластичен и после процедуры термоформовки ботинки получают комфортную, плотную, индивидуальную посадку. Коньки получили новый суппорт голеностопа Curv Composite, который способствует снижению веса, повышает жесткость и плотно охватывает ногу в области голеностопного сустава. Задник ботинка, защищающий ахиллово сухожилие, выполнен по технологии C-Flex и имеет вставки с различным уровнем жесткости, которые позволяют получить три разных уровня жесткости – отсутствие вставки обеспечивает максимальную гибкость и наибольший диапазон движения, вставка Mid обеспечивает традиционную жесткость и более жесткая вставка Stiff обеспечивает дополнительное сопротивление. Ботинок имеет встроенную, стабилизирующую систему шнуровки Stability Lacing System, ее цельная конструкция позволяет плотнее прижать стопу в нижней части ботинка, уменьшает вес и повышает прочность. Люверсы имеют стабилизирующую, Т-образную форму, что исключает необходимость перешнуровывания ботинка во время игры и позволяет сохранить усилие затяжки. Накладки в верхней части ботинок Total Edge Comfort исключают травмирование мягких тканей ноги верхним краем ботинка при агрессивном катании, обеспечивая необходимый комфорт. Обивка в области голеностопного сустава выполнена из легкой пены с эффектом памяти и позволяет убрать свободное пространство внутри ботинка, плотно фиксируя стопу. Язычок так же выполнен с применением технологии C-Flex и имеет вставки с различным уровнем жесткости, которые выполняют защитную функцию и позволяют увеличить диапазон движения и мощность шага. Внутренняя стелька Speed Plate выполнена из пластичного материала и после термоформовки повторяет контур стопы, придает шагу пружинистость, способствует повышению передачи энергии при каждом шаге. Гидрофобный материал с технологией 37.5 используется в качестве внутренней подкладки. Данный материал позволяет использовать тепло тела для быстрого испарения влаги, обеспечивая быстрый отвод тепла и влаги от ноги и создавая исключительный уровень комфорта. На коньках стоят стаканы Tuuk LightSpeed Edge, которые выше на 3 мм, чем традиционные стаканы, что позволило увеличить угол атаки и уменьшить радиус разворота и, как результат, улучшить маневренность игрока. В данной модели установлены профессиональные лезвия из нержавеющей стали Tuuk LightSpeed LS4, имеющие улучшенную отделку поверхности, что обеспечивает более острый край и лезвия лучше держат заточку. Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/912/400_400_140cd750bba9870f18aada2478b24840a/b5821697_b2d1_11e6_9c32_3085a97fbce9_98ac53fc_b2f0_11e6_9c32_3085a97fbce9.jpg?158329855853738', null, 'SUPREME 1S LE', 1, 60, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 1S LE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (59, 'JR', '', 'Описание Коньки BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2019 года. При создании коньков использовалась система ACCELFLEX SYSTEM, эта система обеспечивает высокую эффективность и дополнительную гибкость коньков. Ботинок выполнен из композитного материала 3D LASTED CURV, который обладает очень легким весом и хорошими защитными функциями. Данная модель имеет ассиметричный носок, что придает коньку максимальное удобство. Язычок RECOIL TONGUE из войлока плотностью 48 унций усилен защитной вставкой в области плюсневой кости. Внутренняя подкладка выполнена из нового материала AERO FOAM+, которая обладает улучшенными свойствами во время формовки. Так же для максимального комфорта в области лодыжки используется подкладка COMFORT EDGE Особенности: ✅Уровень – Профессиональный ✅Внешний материал – 3D LASTED CURV ✅Внутренний материал – AERO FOAM+; LOCK-FIT ✅Язык – RECOIL TONGUE ✅Стелька – LOCK-FIT ✅Термоформуемость – Полная ✅Подошва – PRO TPU ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK LS3 (р-р 4-12); TUUK LS2 (р-р 1-3.5) Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5b0/400_400_140cd750bba9870f18aada2478b24840a/2abb498a_762e_11e9_aa99_a4bf0158c9f5_d2812a94_99ab_11e9_aa9a_a4bf0158c9f5.jpg?158331069364462', null, 'VAPOR 2X', 1, 61, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 2X_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (60, 'SR', '', 'Описание Коньки CCM RIBCOR 70K - это топовая модель линейки RIBCOR 2017 года. Полностью новый ботинок, корпус которого выполнен из композитного материала, имеет специальные усиления в запрограммированных зонах для оптимальной продольной гибкости, что дает возможность накапливать и высвобождать энергию при каждом шаге, делая его максимально эффективным, а увеличенная поперечная жесткость позволяет минимизировать деформацию его стенок при нагрузке, повышая динамику катания. Коньки Ribcor 70K предлагают игроку высочайший уровень комфорта сопоставимый с удобными беговыми кроссовками, чего никак не ожидаешь от хоккейных коньков профессионального уровня. Данный эффект достигается за счет новой анатомической формы ботинка и специальной плотной набивки, которая обволакивает стопу игрока и надежно фиксирует ее в ботинке. Дополнительно удерживать ногу позволяют стелька Griptonite, имеющая прорезиненные участки, и специальный материал внутренней отделки TotalDri, эффективно впитывающий влагу и сохраняющий четкий контакт конька с игроком во время всей тренировки. Ботинок модели 70К имеет адаптивную посадку. За счет своей уникальной конструкции он «умеет» подстраиваться под форму стопы, а именно, иметь разную полноту в нескольких частях ботинка. Подобный подход позволяет обеспечить плотную посадку ботинка вдоль всей стопы и предоставить игроку уникальное чувство контроля над коньками. Данный эффект происходит после проведения процедуры термоформовки. Отказ от системы подкачки Pump и замков для шнурков Skate Lock позволило новой модели существенно снизить вес. Так в размере 8D коньки весят всего 790 гр. против 875 гр. у предыдущей модели Ribcor 50К. Новый флагман семейства Ribcor обладает всеми прочими технологиями, присущими модели профессионального уровня. Это язычок TriTech Pro из толстого слоя войлока и плотной пены, которая защищает от ударов и избыточного давления шнурками, легкая композитная подошва с системой отвода влаги. К ботинку приклепаны стаканы SpeedBlade 4.0 для обеспечения лучшей маневренность, в которые установлены лезвия SpeedBlade Black, имеющие специальное оксидированное покрытие для защиты стали от коррозии и увеличивающее интервал между заточками. Характеристики Возрастная группа SR Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/624/400_400_140cd750bba9870f18aada2478b24840a/e828a441_735e_11e7_a985_e0cb4ee6cf35_d65f5a22_767f_11e7_9ca3_3085a97fbce9.jpg?158329058951205', null, 'RIBCOR 70K', 2, 62, 7, 'C:\Users\vinto/images/Коньки хоккейные/CCM_RIBCOR 70K_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (61, 'SR', '', 'Описание Коньки BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. При создании коньков использовалась система PERFORM FLEX для обеспечения комфорта и эффективности. Эта система включает в себя конструкцию ботинка, выполненную из материала 3D LASTED TECH MESH. Благодаря этому материалу ботинок превосходно поддерживает стопу и отличается непревзойденной термоформуемостью. Язычок в данных коньках выполнен из войлока плотностью 48 унций. Внутренняя подкладка выполнена из нового материала AERO FOAM, которая придает коньку максимальное удобство. Так же для максимального комфорта в области лодыжки используется подкладка COMFORT EDGE Особенности: ✅Уровень – Полупрофессиональный ✅Внешний материал – 3D LASTED TECH MESH ✅Внутренний материал – MICROFIBER; AERO FOAM ✅Язык – Войлок плотностью 48 унций ✅Стелька – FORM FIT+ ✅Термоформуемость – Частичная формовка в области лодыжки ✅Подошва – TPU ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK LS1 Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/889/400_400_140cd750bba9870f18aada2478b24840a/4ec38005_980e_11e9_aa9a_a4bf0158c9f5_afbce407_99cf_11e9_aa9a_a4bf0158c9f5.jpg?158328826557060', null, 'VAPOR X2.9', 1, 63, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR X2.9_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (716, 'INT', '', 'Описание Клюшка BAUER VAPOR FLYLITE SHADOW SERIES, лимитированная, топовая модель линейки VAPOR 2019 года. Клюшка представлена полностью в черном цвете и выпущена в ограниченному количестве. Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme; DURAFLEX; ACL • Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE • Конструкция – MONOCOMP • Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b2e/400_400_140cd750bba9870f18aada2478b24840a/177fb3ea_1d95_11ea_aa9a_a4bf0158c9f5_527cfe33_27dd_11ea_aa9a_a4bf0158c9f5.jpg?158327809212720', null, 'VAPOR FLYLITE SHADOW SERIES', 1, 718, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR FLYLITE SHADOW SERIES_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (63, 'SR', '', 'Описание Коньки BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Основные особенности этого конька легкость и надежность. Благодаря композитному материалу 3-D LASTED CURV®, конек после формовки садиться максимально комфортно. Новый язычок REFLEX имеет трехсоставную конструкцию и защиту плюсневой кости. Войлочное покрытие языка 48 унций. Особенности: • Уровень – Профессиональный • Внешний материал – 3-D LASTED CURV® • Внутренний материал – LOCK-FIT • Язык – REFLEX • Стелька – LOCK-FIT • Термоформуемость – Полная • Подошва – Композит • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS3 (р-р 4-12) TUUK LS2 (р-р 1-3.5) Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7c6/400_400_140cd750bba9870f18aada2478b24840a/68b5a78a_6ae3_11e8_995b_3085a97fbce9_451b8977_6442_11ea_aa9b_a4bf0158c9f5.jpg?158402351363209', null, 'SUPREME 2S', 1, 65, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 2S_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (64, 'SR', '', '', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/633/400_400_140cd750bba9870f18aada2478b24840a/978f84b6_0bd1_11e6_9fde_001fc6b58537_c467bd89_391f_11e6_bec3_dba2b096c3df.jpg?158330923364303', null, 'VAPOR 1X', 1, 66, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 1X_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (65, 'SR', '', 'Описание Коньки BAUER VAPOR S17 X900 - это профессиональная модель в линейке VAPOR 2017 года. Какую скорость можно развить на коньках? Примерьте X900 и проверьте сами. Композитный ботинок с эффектом 3D Curv® и система LOCK-FIT+ обеспечивают лучшую посадку, позволяя лучше чувствовать лед. Наша самая жесткая подошва, изготовленная из композитного материала, обеспечивает стабильность на предельных скоростях на протяжении всей игры. Можете ли вы справиться со скоростью? ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитная верхняя часть 3D Curv® с дизайном X-rib • Материал подкладки - Подкладка LOCK FIT+ • Защита лодыжки - Легкие подушки для лодыжек из пены анатомической формы • Конструкция языка - Язычок FLEX-LOCK + • Стелька - Стелька LOCK FIT+ • Термоформуемость - Верхняя часть анатомической формы • Поддержка пятки - Анатомическая поддержка для пятки и лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Легкая, полностью композитная • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - Нержавеющая сталь TUUK LS3 (размеры 4-12), нержавеющая сталь TUUK LS2 (размеры 3-3,5) Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/087/400_400_140cd750bba9870f18aada2478b24840a/2d6faea6_0d35_11e7_9c76_3085a97fbce9_59d21289_581a_11e8_9958_3085a97fbce9.jpg?158328577755093', null, 'VAPOR S17 X900', 1, 67, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 X900_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (66, 'SR', '', 'Описание Коньки CCM JETSPEED FT1 - это топовая модельв линейке JETSPEED 2017 года. Вслед за коньками Super Tacks монолитную конструкцию ботинка получила флагманская модель семейства JetSpeed - FT1. Он выполнен по технологии Liteframe 360 и представляет собой полностью литой ботинок, лишенный привычных элементов, как отдельная подошва. Минимальный набор деталей позволяет наделить новинку высочайшими эксплуатационными характеристиками. Основные особенности новой модели: • Использование в конструкции высокотехнологичного композитного материала позволило создать самые легкие коньки на рынке профессионального уровня. В размере 8D вес составляет всего 772 грамм! При этом ботинок имеет высокую жесткость, что обеспечивает защиту профессионального уровня и невероятные динамические характеристики. • Технология Liteframe 360 позволяет максимально эффективно передавать энергию каждого шага непосредственно на лед, преобразуя ее в максимальную скорость и позволяя всегда быть первым на шайбе. • Ботинок FT1 имеет зауженную посадку для более четкого анатомичного облегания стопы на 360 градусов. Хоккеист получает максимум комфорта и контроля над коньками, что придает уверенности в каждом эпизоде. • На ботинок установлены стаканы SB+4.0, которые позволяют сделать катание максимально маневренным и уменьшить радиус разворота. Лезвие HyperGlide обеспечивает эффективное скольжение по поверхности льда. Максимально яркий и агрессивный дизайн модели FT1 является отражением их неудержимого характера на льду. Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/2e8/400_400_140cd750bba9870f18aada2478b24840a/e828a440_735e_11e7_a985_e0cb4ee6cf35_d65f5a19_767f_11e7_9ca3_3085a97fbce9.jpg?158329428461720', null, 'JETSPEED FT1', 2, 68, 7, 'C:\Users\vinto/images/Коньки хоккейные/CCM_JETSPEED FT1_SR.png');
