INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (68, 'JR', '', 'Описание Коньки BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. При создании коньков использовалась система DYNAFLEX, эта система обеспечивает игроку максимальную производительность при каждом движении. Ботинок выполнен из композитного материала 3D LASTED CURV, который обладает очень легким весом и хорошими защитными функциями. Данная модель имеет ассиметричный носок, что придает коньку максимальное удобство. Благодаря новой композитной шнуровки ботинок лучше прилегает к стопе, поддерживает ее и позволяет игроку лучше контролировать свое катание. Зона лодыжки выполнена из пены с эффектом памяти AERO FOAM PRO, которая обладает улучшенными свойствами во время формовки. В качестве внутренней подкладки используется материал LOCK-FIT PRO, которая эффективно отводит влагу. Данный ботинок ассиметричной клиновидной формы лучше поддерживает стопу во время катания. Особенности: ✅Уровень – Элитный ✅Внешний материал – 3D LASTED CURV ✅Внутренний материал – AERO FOAM PRO; LOCK-FIT PRO ✅Язык – RECOIL PRO TONGUE ✅Стелька – DYNAMIC FLEX ✅Термоформуемость – Полная ✅Подошва – Композит ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK LS5 Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8ea/400_400_140cd750bba9870f18aada2478b24840a/1b7cbbb2_762e_11e9_aa99_a4bf0158c9f5_3545ae5b_99ab_11e9_aa9a_a4bf0158c9f5.jpg?158330666367595', null, 'VAPOR 2X PRO', 1, 70, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 2X PRO_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (69, 'SR', '', 'Описание Коньки BAUER NEXUS 1N, верхняя модель в линейке NEXUS 2016 года. Традиционная широкая посадка, новый современный дизайн конька с заметными синими акцентами. Колодка 1N шире и выше, чем в других моделях коньков Bauer. Верхняя часть конька В коньках Bauer Nexus 1N реализован «адаптивный контроль», благодаря композитному материалу 3-D Curv, с использованием которого конструируется верхний ботинок конька, реализуется максимальный комфорт и эффективная передача энергии хоккеиста. Композитный материал обеспечивает плотное прилегание ботинка к ноге игрока, надежную фиксацию в области пятки и лодыжки для более комфортной посадки и высокой чувствительности. Дополнительные накладки в верхней части суппорта Total Comfort Edge устраняют отсрые края конька, что обеспечивает игроку комфорт при катании. Технология подкладки Технология 37.5 ™ обеспечивает быстрое высыхание коньков между периодами, сохраняя ноги игрока сухими. В Bauer 1N встроены подкладки на лодыжки из пены с эффектом памяти, которые способны принимать индивидуальную форму лодыжек игрока. Они обеспечивают дополнительный комфорт посадки и уменьшение негативного пространства в области голеностопа конька для уверенного контроля на льду. Система шнуровки. В Nexus 1N реализована система фиксации шнуровки Bauer, для гибкости фронтальной части конька и устойчивости на льду. Такая фиксация реализует более эффективный контроль над посадкой благодаря шнуровке. Кроме того, владельцу коньков можно не беспокоиться, что люверсы проржавеют через какое-то время. Язык Язык на Bauer 1N сконструирован из войлока плотностью 52 и 40 унций со вставками CURV из композитного материала. Это дает более тесное прилегание конька к стопе, в результате которого обеспечивается надежная защита и более эффективная поддержка. Стелька Speedplate Уникальная стелька Speedplate имеет свойство большей пружинистости во время толчка на льду. Стелька SpeedPlate способна принимать индивидуальну. форму нижней части стопы игрока, что обеспечивает максимально комфортную посадку. Игрок сможет получить более высокую скоростную отдачу от своего конька. Нижняя часть конька В коньках встроена ультра-легкая композитная дышащая подошва, которая при этом твердая и жесткая. Вентиляционные отверстия доставляют дополнительный приток воздуха, что устраняет лишнюю влагу. Лезвие LS4 сконструировано из инновационного сорта нержавеющей стали премиум-класса. Кроме того, лезвие больше на 3 мм в высоту и имеют усовершенствованный профиль по сравнению с Tuuk LS3. Такая конструкция открывает возможность более четких и более крутых поворотов. Кроме того, усовершенствована система фиксации, что позволяет хоккеисту легко и мгновенно менять лезвия и не пропускать смену. Идеальное решение для тех, кто берет запасные наборы лезвий. Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/38c/400_400_140cd750bba9870f18aada2478b24840a/c0365f0c_4cb9_11e6_88ad_e0cb4ee6cf35_83000e82_4de8_11e6_9c01_3085a97fbce9.jpg?158328722144647', null, 'NEXUS 1N', 1, 71, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_NEXUS 1N_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (70, 'SR', '', 'Описание Коньки BAUER SUPREME 1S, топовая модель в линейке SUPREME 2016 года. Конёк точно повторяет анатомические изгибы стопы и обеспечивает меньшее негативное пространство внутри, в результате конёк имеет плотную, комфортную посадку еще до процесса термоформовки. Ботинок производится из новейшего, композитного материала 3D-lasted Titanium Curv, который значительно снижает вес коньков, обеспечивает повышенную прочность и жесткость. Данный материал пластичен и после процедуры термоформовки ботинки получают комфортную, плотную, индивидуальную посадку. Коньки получили новый суппорт голеностопа Curv Composite, который способствует снижению веса, повышает жесткость и плотно охватывает ногу в области голеностопного сустава. Задник ботинка, защищающий ахиллово сухожилие, выполнен по технологии C-Flex и имеет вставки с различным уровнем жесткости, которые позволяют получить три разных уровня жесткости – отсутствие вставки обеспечивает максимальную гибкость и наибольший диапазон движения, вставка Mid обеспечивает традиционную жесткость и более жесткая вставка Stiff обеспечивает дополнительное сопротивление. Ботинок имеет встроенную, стабилизирующую систему шнуровки Stability Lacing System, ее цельная конструкция позволяет плотнее прижать стопу в нижней части ботинка, уменьшает вес и повышает прочность. Люверсы имеют стабилизирующую, Т-образную форму, что исключает необходимость перешнуровывания ботинка во время игры и позволяет сохранить усилие затяжки. Накладки в верхней части ботинок Total Edge Comfort исключают травмирование мягких тканей ноги верхним краем ботинка при агрессивном катании, обеспечивая необходимый комфорт. Обивка в области голеностопного сустава выполнена из легкой пены с эффектом памяти и позволяет убрать свободное пространство внутри ботинка, плотно фиксируя стопу. Язычок так же выполнен с применением технологии C-Flex и имеет вставки с различным уровнем жесткости, которые выполняют защитную функцию и позволяют увеличить диапазон движения и мощность шага. Внутренняя стелька Speed Plate выполнена из пластичного материала и после термоформовки повторяет контур стопы, придает шагу пружинистость, способствует повышению передачи энергии при каждом шаге. Гидрофобный материал с технологией 37.5 используется в качестве внутренней подкладки. Данный материал позволяет использовать тепло тела для быстрого испарения влаги, обеспечивая быстрый отвод тепла и влаги от ноги и создавая исключительный уровень комфорта. На коньках стоят стаканы Tuuk LightSpeed Edge, которые выше на 3 мм, чем традиционные стаканы, что позволило увеличить угол атаки и уменьшить радиус разворота и, как результат, улучшить маневренность игрока. В данной модели установлены профессиональные лезвия из нержавеющей стали Tuuk LightSpeed LS4, имеющие улучшенную отделку поверхности, что обеспечивает более острый край и лезвия лучше держат заточку. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b22/400_400_140cd750bba9870f18aada2478b24840a/978f8491_0bd1_11e6_9fde_001fc6b58537_763cc52e_391e_11e6_bec3_dba2b096c3df.jpg?158330488551206', null, 'SUPREME 1S', 1, 72, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 1S_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (71, 'SR', '', 'Описание Коньки BAUER VAPOR S17 1X - это топовая модель в линейке VAPOR 2017 года. БОЛЬШЕ КОНТАКТА. БОЛЬШЕ СКОРОСТИ. Получите беспрецедентную посадку и маневренность, которые позволят вам реализовать в игре каждый грамм вложенной вами энергии. 1. Язычок FLEX-LOCK PRO: Термоформованные вставки фиксируют посадку, сохраняя при этом гибкость переднего контура и обеспечивая защиту, удобство и маневренность. 2. Подкладка LOCK-FIT PRO: Обеспечивает сухость и фиксацию ваших ног для максимальной ловкости и ускорения. 3. Шнуровка LOCK-FIT PRO: Благодаря дополнительной фиксации шнуровкой и более мягкому гибкому материалу, коньки 1X с интегрированными вставками надежно зафиксируют ваши ноги и помогут развить максимальную скорость. АСИММЕТРИЧНАЯ ПОСАДКА Производители трансформировали квадратные вставки в соответствии с естественным положением лодыжки, обеспечив вас дополнительной медиальной поддержкой и максимальной гибкостью. УСКОРЬТЕ ИГРУ Производительность и качество, которым вы доверяете. Разработанная лабораторией OD1N, ультралегкая 3D Curv® композитная конструкция X-Rib, усиливает фиксацию пятки и гибкость лодыжки, увеличивая скорость и улучшая маневренность в поворотах. ИГРА НА ОСТРИЕ Запатентованная компанией Bauer система крепления LIGHTSPEED EDGE с курком, позволяет легко и быстро менять затупившееся лезвие. НАКАЛИТЕ ИГРУ Оставьте своего соперника позади благодаря более чистому, надежному и острому лезвию LIGHTSPEED 4 EDGE. Дополнительные 3 мм увеличивают рост и расширяют ваши возможности при нападении. ОПТИМАЛЬНЫЙ БАЛАНС И СТАБИЛЬНОСТЬ SPEED PLATE™ принимает форму стопы, обеспечивая оптимальный баланс и стабильность. Вдобавок увеличение площади контакта между ногой и лезвием способствует улучшению контроля за движениями, необходимого для ускоренного разгона. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитный ботинок с эффектом 3D Curv®, дизайномX-rib и комфортная подкладка для лодыжки • Материал подкладки - Вкладка LOCK-FIT PRO • Защита лодыжки - Легкие, запоминающие форму тела подушки для лодыжек • Конструкция языка Язычок FLEX-LOCK PRO • Стелька - BAUER SPEED PLATE™ • Термоформуемость - Верхняя часть и стелька анатомической формы • Поддержка пятки - Curv® композитная поддержка для пятки и лодыжки НИЖНЯЯ ЧАСТЬ: • Подошва - Полностью карбоновая композитная подошва с вентиляцией • Стакан (держатель для лезвия) - TUUK LIGHTSPEED EDGE • Лезвие - TUUK LS4 из нержавеющей стали Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5a9/400_400_140cd750bba9870f18aada2478b24840a/2d6faea4_0d35_11e7_9c76_3085a97fbce9_1def3e34_302d_11e7_9c8b_3085a97fbce9.jpg?158329058561064', null, 'VAPOR S17 1X', 1, 73, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 1X_SR.png');
