INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (73, 'SR', '', 'Описание Коньки BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2019 года. При создании коньков использовалась система ACCELFLEX SYSTEM, эта система обеспечивает высокую эффективность и дополнительную гибкость коньков. Ботинок выполнен из композитного материала 3D LASTED CURV, который обладает очень легким весом и хорошими защитными функциями. Данная модель имеет ассиметричный носок, что придает коньку максимальное удобство. Язычок RECOIL TONGUE из войлока плотностью 48 унций усилен защитной вставкой в области плюсневой кости. Внутренняя подкладка выполнена из нового материала AERO FOAM+, которая обладает улучшенными свойствами во время формовки. Так же для максимального комфорта в области лодыжки используется подкладка COMFORT EDGE Особенности: ✅Уровень – Профессиональный ✅Внешний материал – 3D LASTED CURV ✅Внутренний материал – AERO FOAM+; LOCK-FIT ✅Язык – RECOIL TONGUE ✅Стелька – LOCK-FIT ✅Термоформуемость – Полная ✅Подошва – PRO TPU ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK LS3 (р-р 4-12); TUUK LS2 (р-р 1-3.5) Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/037/400_400_140cd750bba9870f18aada2478b24840a/065dc9c4_762e_11e9_aa99_a4bf0158c9f5_c8f373fb_99ab_11e9_aa9a_a4bf0158c9f5.jpg?158328829356614', null, 'VAPOR 2X', 1, 75, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 2X_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (74, 'SR', '', 'Описание Коньки CCM RIBCOR 80K, топовая модель линейки CCM 2019 года. Данный конек выполнен из композитного материала и оснащен инновационной технологией FLEXFRAME, благодаря которому ботинок имеет идеальную продольную гибкость и боковую поддержку, что дает возможность накапливать и высвобождать энергию при каждом шаге, делая его максимально эффективным. Для максимального комфорта, в области голеностопа используется пена с эффектом памяти ADPT. На коньках данной модели используется язычок TRITECH PRO, это трехслойный семимиллиметровый войлочный язычок с пеной HDPE, который обеспечивает игрокам не превзойденную защиту и комфорт. Внутренний материал конька оснащен вкладышем TOTAL DRI PRO+, который высокоэффективно отталкивает влагу. На коньках установлены новые стаканы SPEEDBLADE XS. Стаканы имеют новый надежный механизм крепления, который предотвращает движение лезвия, не ослабевает со временем. Новая система крепления позволяет быстро и без лишних инструментов поменять лезвие. Вместе со стаканами, Коньки RIBCORE 80K укомплектованы новыми лезвиями SPEEDBLADE XS1 BLACK. Данное лезвие на 2 мм выше стандартного лезвия, что позволяет улучшить маневренность игрока. Лезвия черного цвета, имеют оксидную обработку, которая жёстче обычной стали. Особенности: • Уровень – Элитный • Внешний материал – Композитный • Внутренний материал – TOTAL DRI PRO+; DURAZONE • Язык – TRITECH PRO • Термоформуемость – Полная • Подошва – Композит • Стакан – SPEEDBLADE XS • Лезвие – SPEEDBLADE XS1 Характеристики Возрастная группа SR Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f62/400_400_140cd750bba9870f18aada2478b24840a/8a0c1111_86ae_11e9_aa99_a4bf0158c9f5_a144a16d_907c_11e9_aa9a_a4bf0158c9f5.jpg?158328107255804', null, 'RIBCOR 80K', 2, 76, 7, 'C:\Users\vinto/images/Коньки хоккейные/CCM_RIBCOR 