INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (722, 'INT', '', 'Описание Клюшка BAUER NEXUS ADV SERIES, ограниченная серия клюшек линейки NEXUS 2020 года. Представляем вашего вниманию самую легкую клюшку в истории NEXUS. Вес данной модели всего 365 грамм достигается за счет комбинации из технологий углового переплетения карбона ACL и усовершенствованной геометрии трубы. Благодаря новой 5-угольной геометрия трубы производителю удалось не только снизить вес, но и повысить игровые характеристики клюшки. Крюк клюшки выполнен по технологии SLINGTECH и имеет совершенно новую конструкцию, благодаря внедрению специального отверстия. Таким образом мы имеем крюк с двумя зонами, которые выполняют совершенно разные функции. Над отверстием, более жесткая зона, сохраняет стабильность крюка во время броска для большей точности. Под отверстием, эластичная зона, придает импульс ускорению шайбы. Основные особенности: ✅Уровень – Элитный ✅Точка прогиба – Средняя ✅Тэйпер – ER SPINE (5-угольная геометрия трубы) ✅Материал трубы – ADVANCED CARBON LAYERING ✅Крюк - SLINGTECH Внимание: На данный товар скидка не действует. Характеристики Возрастная группа INT Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/09d/400_400_140cd750bba9870f18aada2478b24840a/fb3df5d9_38fd_11ea_aa9a_a4bf0158c9f5_29c57220_3f47_11ea_aa9b_a4bf0158c9f5.jpg?158327610813883', null, 'NEXUS ADV SERIES GRIP', 1, 724, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_NEXUS ADV SERIES GRIP_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (486, 'JR', '', 'Описание Трусы CCM SUPER TACKS 9080, профессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется плавающая конструкция с пеной D3O LITE, которая рассеивает энергию удара, эта же пена используется для защиты тазобедренных суставов тела. В области бедра для максимальной защиты используется пена высокой плотности и пластик. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – D3O LITE ✅Защита почек – Пластик ✅Защита тазобедренная – JDP; D30 LITE ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5ff/400_400_140cd750bba9870f18aada2478b24840a/8e8bd284_78a9_11e9_aa99_a4bf0158c9f5_05686e5a_79bf_11e9_aa99_a4bf0158c9f5.jpg?158330288340827', null, 'TACKS 9080', 2, 488, 9, 'C:\Users\vinto/images/Трусы/CCM_TACKS 9080_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (489, 'SR', '', 'Описание Трусы BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Благодаря использованию в трусах новых материалов и усовершенствованных технологий вес был снижен на 20% относительно прошлой линейки. Пена HYPERLITE, AEROLITE 2.0 и композит CURV® обеспечат вам максимальное удобство и легкость без ущерба для защиты. Так же трусы имеют высокопрочный чехол, выполненный из нейлона профессионального уровня плотностью 840 ден. Для вашего удобства в трусах предусмотрена система регулировки, которая позволяет увеличить трусы на 1 дюйм. Особенности: • Уровень – Элитный • Удобство – Клиновидное • Защита позвоночника – CURV®; FREE FLEX • Защита почек – Пена HYPERLITE • Защита тазобедренная – AEROLITE 2.0 • Защита бедра – Пена HYPERLITE • Подкладка – 37.5®; SANITIZED® • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d4a/400_400_140cd750bba9870f18aada2478b24840a/ea8cf6e0_6236_11e8_bc17_e0cb4ee6cf35_885a84d4_6fa4_11e8_995c_3085a97fbce9.jpg?158329349545607', null, 'VAPOR 1X LITE', 1, 491, 9, 'C:\Users\vinto/images/Трусы/BAUER_VAPOR 1X LITE_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (76, 'SR', '', 'Описание Коньки BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Ключевые изменения коснулись внешнего материала, теперь конек выполнен из нового композитного материала 3-D lasted carbon CURV®, который обладает легким весом, лучше подается формовке и придает ботинку повышенную прочность. Новый термоформуемый язычок REFLEX PRO, имеет трехсоставную конструкцию и защиту плюсневой кости. Войлочное покрытие языка 52 унции. Новое лезвие LS5 Carbon выполнено из нержавеющей стали, имеют углеродистое покрытие. Особенности: • Уровень – Элитный • Внешний материал – 3-D Lasted carbon CURV® • Внутренний материал – LOCK-FIT PRO • Язык – REFLEX PRO • Стелька – SPEED PLATE 2.0 • Термоформуемость – Полная и стелька • Подошва – Сверхлегкий композит, вентилируемая • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS5 CARBON Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/22e/400_400_140cd750bba9870f18aada2478b24840a/ea8cf6e7_6236_11e8_bc17_e0cb4ee6cf35_8baa9fc8_6442_11ea_aa9b_a4bf0158c9f5.jpg?158402202966752', null, 'SUPREME 2S PRO', 1, 78, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 2S PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (77, 'SR', '', 'Описание Коньки CCM JETSPEED FT2, топовая модель линейки JETSPEED 2019 года. Ботинок коньков, выполненный по технологии LITEFRAME 360 EVO, имеет без подошвенную, монолитную конструкцию. Данная технология позволяет сделать посадку более плотной и максимально соответствующей анатомии, что повышает площадь контакта между ботинком и стопой игрока, давая непревзойденное чувство контроля и высокую эффективность передачи энергии. Внешний материал ботинка композитное волокно ROCKETFRAME, придает коньку легкий вес, оптимальную жесткость и максимальную защиту. Асимметричная форма ботинка в пяточной области дает естественное повторение контуров стопы, делая поддержку голеностопа более эффективной. В данной модели используется новый стакан SPEEDBLADE XS, главной особенностью которого является система быстрой смены лезвия. Новое лезвие XS1 BLACK имеет оксидированное покрытие, повышающее стойкость к коррозии, а также оно выше на 2 мм, чем стандартные лезвия. Особенности: ✅Уровень – Элитный ✅Внешний материал – ROCKETFRAME ✅Внутренний материал – TOTAL DRI PRO+ ✅Язык – METATOMIC ✅Термоформуемость – Полная ✅Подошва – Отсутствует ✅Стакан – SPEEDBLADE XS ✅Лезвие – SPEEDBLADE XS1 Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/cb7/400_400_140cd750bba9870f18aada2478b24840a/ab0c48ff_59c0_11e9_99c5_3085a97fbce9_ab0c4900_59c0_11e9_99c5_3085a97fbce9.jpg?158328851958095', null, 'JETSPEED FT2', 2, 79, 7, 'C:\Users\vinto/images/Коньки хоккейные/CCM_JETSPEED FT2_SR.png');
