INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (87, 'JR', '', 'Описание Блин с ловушкой BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Блины линейки SUPREME отличаются монолитной конструкцией от основной плиты до торцевой части. Блин S27 оснащен гибкой манжетой FREE-FLEX, которая обеспечивает вратарю повышенную подвижность в области запястья, что позволяет эффективно отбивать шайбы в угол. Блин данной модели подходит для начинающих игроков и игроков-любителей. Особенности: • Уровень – Любитель • Манжета – Запатентованная технология FREE-FLEX • Ладонь - NASH • Лицевая сторона – Пена средней плотности; монолит • Подкладка – Влагоотталкивающий нейлон Ловушка S27 оснащена двойным Т-образным карманом и жесткой манжетой за счет этого она легко открывается и закрывается. Ловушка данной модели подходит для начинающих игроков и игроков-любителей. Особенности: • Уровень – Любительский • Манжета – Монолит • Подкладка – NASH • Карман – Двойной Т-образный • Окантовка – Пена средней плотности Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6ef/400_400_140cd750bba9870f18aada2478b24840a/5523c460_7a47_11e8_995d_3085a97fbce9_8b1b70fc_7a5f_11e8_995d_3085a97fbce9.jpg?158331296329446', null, 'SUPREME S27', 1, 89, 24, 'C:\Users\vinto/images/Блин и ловушка/BAUER_SUPREME S27_JR.png');
