INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (493, 'SR', '', 'Описание Трусы WARRIOR COVERT QRE, топовая модель новой линейки ALPHA 2019 года. • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • ADJUSTABLE LEG EXTENSION – Универсальное решение для растущих игроков. Застежка молния обеспечивает возможность удлинить трусы на 1 дюйм • AXYFLEX KIDNEY AND SPINE PROTECTION – Плавающий внутренний поясной ремень обеспечивает комфорт и подвижность, а также защищает позвоночник и почки • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Характеристики Возрастная группа SR Линейка COVERT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/25b/400_400_140cd750bba9870f18aada2478b24840a/948a95f1_b90e_11e9_aa9a_a4bf0158c9f5_26147d94_bdcb_11e9_aa9a_a4bf0158c9f5.jpg?158329147344783', null, 'COVERT QRE', 3, 495, 9, 'C:\Users\vinto/images/Трусы/WARRIOR_COVERT QRE_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (720, 'JR', '', 'Описание Клюшка BAUER SUPREME ADV, ограниченная серия клюшек линейки SUPREME 2019 года. Представляем вашего вниманию самую легкую клюшку в истории SUPREME. Вес данной модели всего 375 грамм достигается за счет нового уникального семиугольного сечения трубки SONIC TAPER TECHNOLOGY. Новая геометрия тэйпера помогла не только снизить вес клюшки, а также добавить стабильности и мощи при каждом броске. По периметру всей клюшки используется технология углового переплетения карбона ACL. В крюке в качестве внутренней сердцевины используется суперлегкая жесткая пена, которая уменьшает вес крюка и повышает прочность. Так же для более быстрого владения шайбой в середине крюка используется амортизирующая пена. Клюшка имеет оптимизированную точку прогиба, позволяющая прикладывать минимум усилий для максимально мощного броска. Длина производителя - 54 дюймов Полная длина клюшки - 150 см Основные особенности: ✅Уровень – Элитный ✅Точка прогиба – Верхняя, оптимизированная ✅Тэйпер - SONIC TAPER TECHNOLOGY ✅Материал трубы – ADVANCED CARBON LAYERING ✅Крюк - ADVANCED CARBON LAYERING; амортизирующая пена, для более быстрого владения; суперлегкая, жесткая пена, для прочности и минимального веса Внимание: На данный товар скидка не действует. Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d18/400_400_140cd750bba9870f18aada2478b24840a/86c7762f_ebfb_11e9_aa9a_a4bf0158c9f5_1bc135b4_74be_11ea_aa9c_a4bf0158c9f5.jpg?158619224320399', null, 'SUPREME ADV SERIES GRIP', 1, 722, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME ADV SERIES GRIP_JR.png');
