INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (100, 'SR', '', 'Описание Блин с ловушкой BAUER VAPOR 2X, полупрофессиональная модель линейки VAPOR 2019 года. Данный блин был значительно изменен по сравнению с предыдущей коллекцией VAPOR X900. Одно из главных изменений стало, то что блокировочная панель имеет монолитную конструкцию, за счет этого повышается устойчивость блина. Выполнена блокировочная панель из композитного материала CURV, благодаря этому блин стал гораздо легче и прочнее. Для максимального удобства ладонь изготовлена из очень мягкого и надежного материала QUATTRO. Особенности: ✅Уровень – Полупрофессиональный ✅Манжета – Запатентованная технология FREE-FLEX ✅Ладонь - QUATTRO ✅Лицевая сторона – CURV; монолит ✅Подкладка – THERMO MAX Ловушка линейки VAPOR сгибаются под углом 60 градусов и закрывается за счет смыкания всей ладони. Данная модель оснащена новой манжетой, которая обеспечивает большую амплитуду движения. Выполнена манжета из композитного материала CURV, благодаря чему ловушка стала более прочной и структурированной. При изготовлении ловушки использовались новые материалы, чтобы сделать перчатку гибкой и удобной. Помимо этого, была увеличены глубина кармана. Особенности: ✅Уровень – Полупрофессиональный ✅Ключевые изменения – Новый глубокий карман; Новый ремень FAST STRAP; Манжета FREE-FLEX с композитным материалом CURV ✅Манжета – CURV; FREE-FLEX ✅Подкладка – THERMO MAX ✅Карман – Т-образный ✅Окантовка – Пена средней плотности Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c0d/400_400_140cd750bba9870f18aada2478b24840a/611a930b_8db8_11e9_aa99_a4bf0158c9f5_738a737d_9077_11e9_aa9a_a4bf0158c9f5.jpg?158331099046222', null, 'VAPOR 2X', 1, 102, 24, 'C:\Users\vinto/images/Блин и ловушка/BAUER_VAPOR 2X_SR.png');
