INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (119, 'YTH', '', 'Описание Щитки вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Щитки подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал щитков SPEEDSKIN, который обеспечивает максимальное скольжения. Щиток имеет хорошую гибкую конструкцию с одним изломом. С обратной стороны расположена быстросъемная система ремней. Колено в данной модели регулируемое, благодаря этому вы можете брать щитки на вырост. Особенности: ✅Возраст – Дети (4-7 лет) ✅Конструкция – Мягкая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/9aa/400_400_140cd750bba9870f18aada2478b24840a/356f7c41_5b5c_11e9_99c5_3085a97fbce9_531da691_79c0_11e9_aa99_a4bf0158c9f5.jpg?158329555158265', null, 'YFLEX 2', 2, 121, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_YFLEX 2_YTH.png');
