INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (121, 'JR', '', 'Описание Щитки BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Данные модель имеет конструкцию DINAMIC FLEX и ботинок с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Любительский ✅Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – Модернизированный синтетический ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/09c/400_400_140cd750bba9870f18aada2478b24840a/d9d7c251_9195_11e9_aa9a_a4bf0158c9f5_83a33e92_932d_11e9_aa9a_a4bf0158c9f5.jpg?158329555533934', null, 'VAPOR X2.7', 1, 123, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_VAPOR X2.7_JR.png');
