INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (132, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX 4 PRO, топовая модель линейки EXTREME FLEX 2019 года. Данная модель отличается высоким уровнем защиты, максимальной гибкостью и производительностью. Внешний материал щитков выполнен из высокопрочного синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Внутренний двойной сердечник использованный в данной модели, позволил уменьшить вес щитка на 15% в сравнении с предыдущей коллекцией. Сердечник дает ощущение торсионной гибкости под коленом. Это значит, что щиток повторит за вами все движения. Над коленом расположен более жесткий сердечник, который обеспечивает устойчивость. Ботинок имеет угол излома в 90 градусов. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. В сравнении с щитками EXTREME FLEX 4.9, модель EXTREME FLEX 4 PRO будет иметь более качественную конструкцию с использованием материалов более высокого уровня. Особенности: ✅Уровень – Элитный ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/28e/400_400_140cd750bba9870f18aada2478b24840a/abda8ef3_8ea2_11e9_aa99_a4bf0158c9f5_f123091d_9468_11e9_aa9a_a4bf0158c9f5.jpg?158329164254374', null, 'EXTREME FLEX 4 PRO', 2, 134, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_EXTREME FLEX 4 PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (513, 'JR', '', 'Описание Наколенники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. В области икроножной мышцы использована формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется влагоотталкивающий материал. Особенности: • Уровень – Полупрофессиональный • Защита икры – Формованная пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка – PE FOAM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/59d/400_400_140cd750bba9870f18aada2478b24840a/fe43db1b_af54_11e8_9963_3085a97fbce9_fe43db22_af54_11e8_9963_3085a97fbce9.jpg?158331094164162', null, 'JETSPEED FT370', 2, 515, 5, 'C:\Users\vinto/images/Щитки/CCM_JETSPEED FT370_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (125, 'SR', '', 'Описание Щитки вратаря CCM PREMIER P2.5, любительская модель линейки PREMIER 2018 года. Внешний материал щитков SPEEDSKIN, который лучше скользит по льду. Внутренняя конструкция щитков выполнена по новой технологии LITECORE, благодаря вес снизился на 20%, чем предыдущая коллекция и улучшен отскок шайбы во время игры. Так же в этих щитках изменена нижняя часть, закрывающая конек, теперь вратарю будет проще отталкиваться ото льда, стоя на коленях. С обратной стороны щитки оснащены эластичными ремешками STRAP SYSTEM, благодаря чему вратарю проще садиться в бабочку и вставать на ноги. В сравнении с щитками PREMIER P2.9, модель PREMIER P2.5 будет иметь менее качественную конструкцию с использованием материалов более низкого уровня. Особенности: ✅Уровень – Любительский ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/775/400_400_140cd750bba9870f18aada2478b24840a/b4198339_b654_11e8_9964_3085a97fbce9_b419833a_b654_11e8_9964_3085a97fbce9.jpg?158331371774021', null, 'PREMIER P2.5', 2, 127, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_PREMIER P2.5_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (126, 'INT', '', 'Описание Щитки BAUER VAPOR 2X, полупрофессиональная модель линейки VAPOR 2019 года. Щиток данной модели потерпел колоссальные изменения по сравнению с щитками предыдущей коллекции VAPOR X900. Теперь они оснащены конструкцией DINAMIC FLEX и ботинком с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. В области бедра используется композитный материал CURV, благодаря этому щиток имеет очень легкий вес и стал более прочным и гибким. В области колена и коленного блока используется материал C.O.R.T.E.C.H ST, которое обеспечивает лучшее скольжение по сравнению с обычной искусственной кожей. Для защиты икроножной мышцы используется пена AEROLITE. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Полупрофессиональный ✅ Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – C.O.R.T.E.C.H ST; Искусственная кожа ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c6c/400_400_140cd750bba9870f18aada2478b24840a/2c467260_8db8_11e9_aa99_a4bf0158c9f5_12bcc1da_926d_11e9_aa9a_a4bf0158c9f5.jpg?158329555676013', null, 'VAPOR 2X', 1, 128, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_VAPOR 2X_INT.png');
