INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (514, 'YTH', '', 'Описание Наколенники BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет анатомическую посадку. Для защиты колена и бедра используется пена средней плотности. Область икры выполнена из пены VENT ARMOR, которая придает наколеннику более легкий вес и хорошо поглощает энергию удара. Для максимального удобства, в качестве регулировки, используется мягкая эластичная липучка. Внутренняя подкладка выполнена по технологии THERMO MAX+, благодаря этому наколенник быстро сохнет. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита икры – VENT ARMOR ✅Боковая защита – Пена средней плотности ✅Защита колена – Пена средней плотности ✅Защита бедра – Пена средней плотности ✅Крепление – 1 эластичная широкая липучка ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7ad/400_400_140cd750bba9870f18aada2478b24840a/50cfbd20_7639_11e9_aa99_a4bf0158c9f5_135b75e0_99d4_11e9_aa9a_a4bf0158c9f5.jpg?158329625658095', null, 'SUPREME 2S PRO', 1, 516, 5, 'C:\Users\vinto/images/Щитки/BAUER_SUPREME 2S PRO_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (519, 'JR', '', 'Описание Наколенники CCM TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. В области икроножной мышцы для больше защиты используется пена средней плотности и пластик. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Полупрофессиональный ✅Защита икры – Пена средней плотности; пластик ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d08/400_400_140cd750bba9870f18aada2478b24840a/4b562d93_9812_11e9_aa9a_a4bf0158c9f5_faffa767_99d5_11e9_aa9a_a4bf0158c9f5.jpg?158331347160163', null, 'TACKS 9060', 2, 521, 5, 'C:\Users\vinto/images/Щитки/CCM_TACKS 9060_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (723, 'INT', '', 'Описание Клюшка BAUER SUPREME ADV, ограниченная серия клюшек линейки SUPREME 2019 года. Представляем вашего вниманию самую легкую клюшку в истории SUPREME. Вес данной модели всего 375 грамм достигается за счет нового уникального семиугольного сечения трубки SONIC TAPER TECHNOLOGY. Новая геометрия тэйпера помогла не только снизить вес клюшки, а также добавить стабильности и мощи при каждом броске. По периметру всей клюшки используется технология углового переплетения карбона ACL. В крюке в качестве внутренней сердцевины используется суперлегкая жесткая пена, которая уменьшает вес крюка и повышает прочность. Так же для более быстрого владения шайбой в середине крюка используется амортизирующая пена. Клюшка имеет оптимизированную точку прогиба, позволяющая прикладывать минимум усилий для максимально мощного броска. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Основные особенности: ✅Уровень – Элитный ✅Точка прогиба – Верхняя, оптимизированная ✅Тэйпер - SONIC TAPER TECHNOLOGY ✅Материал трубы – ADVANCED CARBON LAYERING ✅Крюк - ADVANCED CARBON LAYERING; амортизирующая пена, для более быстрого владения; суперлегкая, жесткая пена, для прочности и минимального веса Внимание: На данный товар скидка не действует. Характеристики Возрастная группа INT Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e86/400_400_140cd750bba9870f18aada2478b24840a/63bdc761_ebfb_11e9_aa9a_a4bf0158c9f5_1052f87e_74be_11ea_aa9c_a4bf0158c9f5.jpg?158625435020399', null, 'SUPREME ADV SERIES GRIP', 1, 725, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME ADV SERIES GRIP_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (129, 'SR', '', 'Описание Щитки BAUER VAPOR 2X, полупрофессиональная модель линейки VAPOR 2019 года. Щиток данной модели потерпел колоссальные изменения по сравнению с щитками предыдущей коллекции VAPOR X900. Теперь они оснащены конструкцией DINAMIC FLEX и ботинком с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. В области бедра используется композитный материал CURV, благодаря этому щиток имеет очень легкий вес и стал более прочным и гибким. В области колена и коленного блока используется материал C.O.R.T.E.C.H ST, которое обеспечивает лучшее скольжение по сравнению с обычной искусственной кожей. Для защиты икроножной мышцы используется пена AEROLITE. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Полупрофессиональный ✅ Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – C.O.R.T.E.C.H ST; Искусственная кожа ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/1fb/400_400_140cd750bba9870f18aada2478b24840a/fb7b1381_8db7_11e9_aa99_a4bf0158c9f5_8b76c840_926b_11e9_aa9a_a4bf0158c9f5.jpg?158335176364661', null, 'VAPOR 2X', 1, 131, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_VAPOR 2X_SR.png');
