INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (150, 'JR', '', 'Описание Трусы вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра- Пена высокой плотности; ✅Защита спины- FREE FLEX; DINAMIC 2.FLEX ✅Подкладка – THERMO MAX Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/336/400_400_140cd750bba9870f18aada2478b24840a/08deccbb_8dba_11e9_aa99_a4bf0158c9f5_41cf127c_9267_11e9_aa9a_a4bf0158c9f5.jpg?158331117146311', null, 'VAPOR X2.9', 1, 152, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_VAPOR X2.9_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (151, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Любительский • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – MD FOAM • Защита спины – Пена • Подкладка – Влагоотталкивающая Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b8e/400_400_140cd750bba9870f18aada2478b24840a/b9535a59_865d_11e8_995e_3085a97fbce9_b9535a5a_865d_11e8_995e_3085a97fbce9.jpg?158328201141069', null, 'SUPREME S27', 1, 153, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_SUPREME S27_SR.png');
