INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (526, 'JR', '', 'Описание Наколенники Bauer Vapor 1X – это верхняя профессиональная модель линейки Vapor 2016 года. Особенности: • Конусная посадка щитков Bauer Vapor 1X, широкая вверху и сужающаяся к голени, обеспечивает необходимую скорость и маневренность игрока. • Соответствуя высшему уровню стандартов по безопасности и защите, щитки Bauer Vapor 1X позволяют игроку быть максимально гибким и мобильным. • Баланс профессиональной защиты от ударов и минимального веса щитка достигается путем сочетания технологии Free Flex Curv и Aerolite • Применение гибких сегментов Free Flex в щитке обеспечивает игроку большую свободу движений и удобство. • Встроенная в подкладку инновационная технология 37.5 удаляет влагу, испаряя пот и делая щитки легче. Экипировка остается легкой на протяжении всей игры и поддерживает оптимальную температуру тела. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/2dc/400_400_140cd750bba9870f18aada2478b24840a/aa6bc9a0_142b_11e6_b103_85ec36a0f0cc_17ac9b33_2ad4_11e6_beb1_8807972027df.jpg?158329663049738', null, 'VAPOR 1X', 1, 528, 5, 'C:\Users\vinto/images/Щитки/BAUER_VAPOR 1X_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (155, 'INT', '', 'Описание Трусы вратаря BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Защита тазобедренных суставов имеет плавающую конструкцию и выполнена с использованием пены PORON XRD, которая эффективно поглощает энергию удара, создавая отличную защиту. Набедренные щитки обладают отличными защитными свойствами, точно повторяют анатомические контуры тела, обеспечивая комфортную посадку и плотное прилегание элементов защиты. Внутренняя подкладка выполнена из материала THERMO MAX. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Полупрофессиональный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – PORON XRD • Защита спины – Пена • Подкладка – THERMO MAX Характеристики Возрастная группа INT Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/1d9/400_400_140cd750bba9870f18aada2478b24840a/4d4acd80_d600_11e8_9984_3085a97fbce9_154e192f_d74f_11e8_9984_3085a97fbce9.jpg?158331093241556', null, 'SUPREME S29', 1, 157, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_SUPREME S29_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (156, 'INT', '', 'Описание Трусы вратаря BAUER VAPOR X2.9, полупрофессиональная модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. Особенности: ✅Уровень – Полупрофессиональный ✅Защита бедра- Пена высокой плотности; ✅Защита спины- FREE FLEX; DINAMIC 2.FLEX ✅Подкладка – THERMO MAX Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a51/400_400_140cd750bba9870f18aada2478b24840a/25052e0a_8dba_11e9_aa99_a4bf0158c9f5_41cf1282_9267_11e9_aa9a_a4bf0158c9f5.jpg?158331165345658', null, 'VAPOR X2.9', 1, 158, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_VAPOR X2.9_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (157, 'INT', '', 'Описание Трусы вратаря CCM EXTREME FLEX SHIELD E2.9, профессиональная модель линейки EXTREME FLEX 2018 года. Данная модель имеет такую же конструкцию, как линейка PREMIER в области талии и ниже. Трусы линейки EXTREME FLEX более плотней прилегают к талии и сидят как влитые, что намного лучше с точки зрения комфорта. С изнанки предусмотрена специальная система, благодаря которой шорты сидят очень плотно на бедрах. В области тазобедренных суставах используется сегментированная пена высокой плотности. Для максимальной защиты в области бедра используется пластик с вставками из полиэтилена. Главное отличие от PREMIER заключается в том, как сидят трусы в области талии. Так же трусы имеют систему регулировки, благодаря которой трусы можно опустить на 1 дюйм (2,5СМ). Чехол в данной модели выполнены из нейлона плотность 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; полиэтилен ✅Защита тазобедренная – Пена высокой плотности ✅Защита позвоночника – Пена высокой плотности ✅Регулировка – По длине на 1 дюйм (2,5 см); внутренние ремни по объему ✅Чехол –Нейлон 400 ден Характеристики Возрастная группа INT Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/478/400_400_140cd750bba9870f18aada2478b24840a/f3002066_af6f_11e8_9963_3085a97fbce9_f3002067_af6f_11e8_9963_3085a97fbce9.jpg?158331113339834', null, 'EXTREME FLEX SHIELD E2.9', 2, 159, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_EXTREME FLEX SHIELD E2.9_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (529, 'SR', '', 'Описание Наколенники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры для большей защиты используется формованный полиэтилен. Для защиты бедра и колена используется пена средней плотности. Внутренняя подкладка THERMO MAX, которая отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита икры – Полиэтилен ✅Боковая защита – MD FOAM(пена средней плотности) ✅Защита бедра – MD FOAM(пена средней плотности) ✅Крепление – Два ремня фиксируются сверху и снизу Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/861/400_400_140cd750bba9870f18aada2478b24840a/d34643c9_9811_11e9_aa9a_a4bf0158c9f5_44af48ed_99a9_11e9_aa9a_a4bf0158c9f5.jpg?158331245362069', null, 'SUPREME S29', 1, 531, 5, 'C:\Users\vinto/images/Щитки/BAUER_SUPREME S29_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (163, 'SR', '', 'Описание Трусы вратаря CCM PREMIER R1.9 LE, профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Все защитные элементы выполнены из высококачественного полиэтилена. Область позвоночника выполнена из материала EVA. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Защита позвоночника - EVA ✅Регулировка – Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа SR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/eb1/400_400_140cd750bba9870f18aada2478b24840a/0c0135b2_b44b_11e9_aa9a_a4bf0158c9f5_db897fc1_b9e2_11e9_aa9a_a4bf0158c9f5.jpg?158331130842771', null, 'PREMIER R1.9 LE', 2, 165, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_PREMIER R1.9 LE_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (164, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME 2S, топовая модель линейки SUPREME 2018 года. Для защиты бедер использована пена POWERLITE, это смесь пены высокой плотности, пены EPP и композитного материала CURV, обеспечивающая вратарю надежную защиту. В области таза использован материал XRD для того чтоб вратарь был всегда защищен во время перемещений. Внутри трусов находится специальный пояс, используя который вратарь может наиболее удобным для себя образом отрегулировать эти трусы в области талии. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Внутренняя подкладка трусов 37,5, которая обработана по технологии SANITIZED. Особенности: • Уровень – Элитный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – POWERLITE • Защита спины – POWERLITE • Подкладка – 37,5; SANITIZED Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b21/400_400_140cd750bba9870f18aada2478b24840a/b9535a45_865d_11e8_995e_3085a97fbce9_b9535a46_865d_11e8_995e_3085a97fbce9.jpg?158331047340975', null, 'SUPREME 2S PRO', 1, 166, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_SUPREME 2S PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (530, 'JR', '', 'Описание Наколенники BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Одним из главных достижений разработчиков этой модели можно считать снижение веса примерно на 10% относительно предыдущей коллекции. Такого значительного изменения веса удалось добиться за счет использования уникальной новейшей технологии AEROLITE 2.0. Благодаря легким материалам и новейших технологий наколенники обеспечивают максимальное удобство и мобильность. Щитки имеют клиновидную посадку. Особенности: • Уровень – Элитный • Посадка - Клиновидная • Защита голени – AEROLITE 2.0; CURV®; технология FREE FLEX • Защита икры – Пена HYPERLITE • Боковая защита – Пена HYPERLITE • Защита бедра – Пена средней плотности • Крепление – Два ремня фиксируются сверху и снизу • Подкладка - Технология 37,5®;SANITIZED®. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a3f/400_400_140cd750bba9870f18aada2478b24840a/68b5a793_6ae3_11e8_995b_3085a97fbce9_885a84da_6fa4_11e8_995c_3085a97fbce9.jpg?158331232355884', null, 'VAPOR 1X LITE', 1, 532, 5, 'C:\Users\vinto/images/Щитки/BAUER_VAPOR 1X LITE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (166, 'SR', '', 'Описание Трусы вратаря CCM EXTREME FLEX SHIELD II, топовая модель линейки EXTREME FLEX 2018 года. Данная модель имеет такую же конструкцию, как линейка PREMIER в области талии и ниже. Трусы линейки EXTREME FLEX более плотней прилегают к талии и сидят как влитые, что намного лучше с точки зрения комфорта. С изнанки предусмотрена специальная система, благодаря которой шорты сидят очень плотно на бедрах. Для максимальной защиты в области тазобедренных суставах используется пена D3O, которая максимально поглощает энергию удара. Главное отличие от PREMIER заключается в том, как сидят трусы в области талии. Так же трусы имеют систему регулировки, благодаря которой трусы можно опустить на 1 дюйм (2,5СМ). Чехол в данной модели выполнены из высококачественного текстурированного нейлона плотность 420 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Элитный ✅Защита бедра – Пена высокой плотности ✅Защита тазобедренная – D3O ✅Защита позвоночника - Пена высокой плотности ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Высокопрочный абразивный нейлон 420 ден Характеристики Возрастная группа SR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/fea/400_400_140cd750bba9870f18aada2478b24840a/0fe7f87e_9a0b_11e8_995f_3085a97fbce9_0fe7f87f_9a0b_11e8_995f_3085a97fbce9.jpg?158331468543056', null, 'EXTREME FLEX SHIELD II', 2, 168, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_EXTREME FLEX SHIELD II_SR.png');
