INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (171, 'JR', '', 'Описание Наплечник вратаря BAUER SUPREME S27, любительская модель линейки BAUER 2018 года. Защиту наплечника усилили на 30% относительно прошлой линейки SUPREME. Для улучшения защиты добавили вставки из пены высокой плотности в область плеч, живота и рук. Также добавили возможность регулировки рук по длине на пол дюйма, чтобы обеспечить максимальный комфорт во время движения. Особенности: • Уровень – Любительский • Регулировка – Два эластичных ремня в области спины • Защита плеч – Пена высокой плотности • Защита рук – Пена высокой плотности • Защита груди – Пена высокой плотности • Защита ребер – Пена высокой плотности • Подкладка –Влагоотталкивающий материал Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a7e/400_400_140cd750bba9870f18aada2478b24840a/d5940f0b_d2d9_11e9_aa9a_a4bf0158c9f5_2591a9e8_d3ce_11e9_aa9a_a4bf0158c9f5.jpg?158331365561965', null, 'SUPREME S27', 1, 173, 19, 'C:\Users\vinto/images/Вратаские нагрудники/BAUER_SUPREME S27_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (533, 'JR', '', 'Описание Наколенники CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. Главное отличие этих щитков от FT1 в том, что в области икры вместо композитного материала ROCKETFRAME, используется формованная пена высокой плотности с пластиковыми вставками. В защите бедра используется формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется технология DRYFORM. Особенности: • Уровень – Профессиональный • Защита икры – Пластик; Пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка - DRYFORM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ac7/400_400_140cd750bba9870f18aada2478b24840a/fe43db1d_af54_11e8_9963_3085a97fbce9_fe43db26_af54_11e8_9963_3085a97fbce9.jpg?158331141365304', null, 'JETSPEED FT390', 2, 535, 5, 'C:\Users\vinto/images/Щитки/CCM_JETSPEED FT390_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (538, 'SR', '', 'Описание Наколенники Bauer Vapor 1X – это верхняя профессиональная модель линейки Vapor 2016 года. Особенности: • Конусная посадка щитков Bauer Vapor 1X, широкая вверху и сужающаяся к голени, обеспечивает необходимую скорость и маневренность игрока. • Соответствуя высшему уровню стандартов по безопасности и защите, щитки Bauer Vapor 1X позволяют игроку быть максимально гибким и мобильным. • Баланс профессиональной защиты от ударов и минимального веса щитка достигается путем сочетания технологии Free Flex Curv и Aerolite • Применение гибких сегментов Free Flex в щитке обеспечивает игроку большую свободу движений и удобство. • Встроенная в подкладку инновационная технология 37.5 удаляет влагу, испаряя пот и делая щитки легче. Экипировка остается легкой на протяжении всей игры и поддерживает оптимальную температуру тела. Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/61c/400_400_140cd750bba9870f18aada2478b24840a/aa6bc99f_142b_11e6_b103_85ec36a0f0cc_17ac9b31_2ad4_11e6_beb1_8807972027df.jpg?158330211149738', null, 'VAPOR 1X', 1, 540, 5, 'C:\Users\vinto/images/Щитки/BAUER_VAPOR 1X_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (175, 'JR', '', 'Описание Наплечник вратаря CCM EXTREME FLEX SHIELD E2.5, юниорская профессиональная модель линейки EXTREME FLEX 2018 года. Наплечник обладает максимальной мобильностью и отличными защитными функциям. Все защитные элементы наплечника выполнены из высококачественного полиэтилена, благодаря этому наплечник имеет легкий вес и подвижность при максимальной защите. Данная модель имеет систему регулировки VELCO ADJUSTMENT, благодаря чему наплечник можно максимально подогнать по телу. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен ✅Защита плеч – Полиэтилен ✅Защита живота – Сегментированная конструкция из полиэтилена Характеристики Возрастная группа JR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/dc0/400_400_140cd750bba9870f18aada2478b24840a/356f7c59_5b5c_11e9_99c5_3085a97fbce9_356f7c5a_5b5c_11e9_99c5_3085a97fbce9.jpg?158331129153348', null, 'EXTREME FLEX SHIELD E2.5', 2, 177, 19, 'C:\Users\vinto/images/Вратаские нагрудники/CCM_EXTREME FLEX SHIELD E2.5_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (176, 'JR', '', 'Описание Наплечник вратаря CCM PREMIER R1.5, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель предназначена для вратарей, которые хотят оставить, как можно меньше места в воротах. Производитель создал квадратные рукава, чтобы облегчить движения рук вдоль тела, а также оптимизировал ширину, чтобы обеспечить максимальную зону охвата. Область рук и груди выполнена из высококачественного полиэтилена, который обеспечивает отличный уровень защиты. Для защиты плеч используется пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Область живота имеет сегментированные вставки из высококачественного полиэтилена, которые обеспечивают максимальную подвижность и хорошие защитные свойства. Система регулировки наплечника позволяет Вам, максимально комфорта настроить его под себя. Особенности: ✅Уровень – Профессиональный ✅Защита груди – Полиэтилен ✅Защита рук и локтя – Полиэтилен; JDP ✅Защита плеч – Пена высокой плотности ✅Защита живота – Сегментированные вставки из полиэтилена Характеристики Возрастная группа JR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a16/400_400_140cd750bba9870f18aada2478b24840a/34ee3af4_8ea2_11e9_aa99_a4bf0158c9f5_c6d4040f_9260_11e9_aa9a_a4bf0158c9f5.jpg?158331116540031', null, 'PREMIER R1.5 LE', 2, 178, 19, 'C:\Users\vinto/images/Вратаские нагрудники/CCM_PREMIER R1.5 LE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (178, 'INT', '', 'Описание Наплечник вратаря BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Для лучшей защиты в наплечнике использована пена высокой плотности в область предплечья и рук. Пеной высокой плотности так же усилены плечевые чашки, а в области грудной клетки использован материал VENT ARMOR. Защита локтей выполнена из пены PORON XRD. Особенности: • Уровень – Полупрофессиональный • Регулировка – Два эластичных ремня в области спины • Защита плеч – Легкая пена высокой плотности • Защита рук – Пена высокой плотности; PORON XRD • Защита груди – VENT ARMOR • Защита ребер – Пена высокой плотности • Подкладка – THERMO MAX Характеристики Возрастная группа INT Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7aa/400_400_140cd750bba9870f18aada2478b24840a/b9535a5f_865d_11e8_995e_3085a97fbce9_b9535a60_865d_11e8_995e_3085a97fbce9.jpg?158331179349256', null, 'SUPREME S29', 1, 180, 19, 'C:\Users\vinto/images/Вратаские нагрудники/BAUER_SUPREME S29_INT.png');
