INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (186, 'SR', '', 'Описание Наплечник вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. В сравнении с предыдущей модель 1Х, наплечник 2Х PRO претерпел значительные изменения. Рукава теперь отличаются трехточечной системой регулировки, за счет чего достигается большая амплитуда движения. В области рукавов используется композитный материал CURV®, который придает наплечнику очень легкий вес и хорошие защитные свойства. А плечевые чаши выполнены из пены AEROLITE. Помимо этого, наплечник оснащен системой ARCH ADJUST, посредством которой его можно подогнать по фигуре, отрегулировав спереди и сзади. Особенности: ✅Уровень – Элитный ✅Регулировка – Два эластичных ремня в области спины; Рукава; Плечи ✅Защита плеч – AEROLITE ✅Защита рук – CURV®; FREE FLEX ✅Защита груди – CURV® ✅Защита ребер – Пена высокой плотности ✅Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ecb/400_400_140cd750bba9870f18aada2478b24840a/f102e0b6_8db8_11e9_aa99_a4bf0158c9f5_fb7b15fa_91ce_11e9_aa9a_a4bf0158c9f5.jpg?158331535049919', null, 'VAPOR 2X PRO', 1, 188, 19, 'C:\Users\vinto/images/Вратаские нагрудники/BAUER_VAPOR 2X PRO_SR.png');
