INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (211, 'SR', '', 'Описание Раковина вратаря BAUER S18 SUPREME SR Раковина обеспечивает защиту профессионального уровня и выполнена из легких и долговечных материалов, обладает хорошей мобильностью, не ограничивает движений вратаря. Внешняя отделка бандажа выполнена из износостойкого нейлона. Передняя панель раковины, закрывающая и защищающая нижнюю часть живота и область тазобедренных суставов, имеет эргономичную форму и сегментированную конструкцию, которая позволяет вратарю свободно двигаться. Пена двойной плотности используется в качестве амортизирующего наполнения передней панели. Вставка, выполненная по технологии PORON XRD, обеспечивает вратарю максимальный уровень защиты. ополнительную защиту для паха и внутренней области бедра создают комфортные накладки COMFORT EDGE из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт при использовании раковины. Подходит примерно на возраст от 14 лет Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/f52/0daedeb8_623b_11e8_9958_3085a97fbce9_61732d0c_66a2_11e8_9959_3085a97fbce9.jpg?1557611006280961', null, 'S18 SUPREME', 1, 213, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/BAUER_S18 SUPREME_SR.png');
