INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (572, 'TACKS', '', 'Описание Шлем CCM TACKS 710, топовая модель линейки CCM 2018 года. Шлем был разработан для обеспечения максимального комфорта и удобства. Шлем имеет ударопрочный, поликарбонатный корпус. Первый слой внутренней подкладки выполнен по технологии R.E.D. SYSTEM, которая защищает от прямых и касательных ударов. Механизм защиты от ударов по касательной построен по принципу, частичного гашения силы удара при небольшом повороте системы амортизаторов относительно головы за счет емкостей с жидкостью. Внутренний слой, который соприкасается с головой, выполнен из пены I.Q.SHION, которая обеспечивает превосходный уровень комфорта благодаря эффекту памяти. Височная область выполнена из пены D3O, которая обладает высокой степенью поглощения ударного воздействия и уменьшает энергию удара во время игры. Особенности: • Уровень – Элитный • Регулировка - Центральная, без использования инструментов • Ключевые технологии – D3O; I.Q.SHION; R.E.D. SYSTEM • Сертифицирован – CSA, HECC и европейский знак соответствия требованиям безопасности CE Маска CCM FM680 Маска выполнена из высококачественной углеродистой стали и имеет плоское сечение прутьев, обеспечивая максимальную видимость. Подбородник выполнен из пены двойной плотности. Характеристики Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b48/400_400_140cd750bba9870f18aada2478b24840a/208ff909_9c31_11e7_9cb4_3085a97fbce9_b482231e_a2f8_11e7_9cb9_3085a97fbce9.jpg?158330083366118', null, '710 COMBO', 2, 574, 6, 'C:\Users\vinto/images/Шлем/CCM_710 COMBO_TACKS.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (213, 'SR', '', 'Описание Раковина вратаря CCM 500 Вешняя отделка раковины выполнена из мягких, воздухопроницаемых материалов, отличающихся высокой износоустойчивостью. Раковина комплектуется стандартной, защитной чашей из формованного пластика, которая покрывается мягким, вспененным материалом, что создает эффективную защиту области паха и способствует комфортному прилеганию чаши к телу. Дополнительную защиту для паха и внутренней области бедра создают специальные накладки из вспененного материала, которые способствуют более мягкому контакту элементов защиты с телом, существенно повышают комфорт. Передняя панель, защищающая нижнюю часть живота и область тазобедренных суставов, выполнена из вспененного материала и имеет удобную, эргономичную форму. Сегментированное строение передней панели не ограничивает движения вратаря и способствует комфортной и плотной посадке раковины на теле. Подходит примерно на возраст от 14 лет Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/bac/f5ce8295_0bd0_11e6_9fde_001fc6b58537_bee7e806_392b_11e6_bec4_de6fc43a47df.jpg?1557604716157870', null, '500', 2, 215, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_500_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (214, 'SR', '', 'Описание Защита коленей вратаря CCM 1.5 SR Наколенники создают дополнительный уровень защиты коленного сустава. Высокопрочный нейлон используется в качестве материала внешней отделки наколенников. Легкая пена, формованная для достижения удобной посадки, используется для внутреннего наполнения и обеспечивает удобную посадку. Регулируемая система крепления имеет одни эластичный ремень шириной 5 см для фиксации на бедре и два эластичных ремня шириной 3,8 см для фиксации под коленом и ниже колена. Данный размер подходит на возраст от 14 лет Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/80d/be308e29_9a03_11e7_9cb4_3085a97fbce9_c8f88e9d_a2ef_11e7_9cb9_3085a97fbce9.jpg?1557611577258468', null, '1.5', 2, 216, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_1.5_SR.png');
