INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (219, 'INT', '', 'Описание Защита коленей вратаря CCM 1.9 Наколенники имеют подвижную, двухкомпонентную конструкцию, которая не ограничивает движений вратаря. Внешняя отделка выполнена из высокопрочного нейлона, устойчивого к истиранию и механическим нагрузкам. Конструкция коленной чаши имеет глубокое посадочное место, что обеспечивает дополнительную поддержку колена. Формованная, коленная чаша выполнена из ударопрочного пластика, обеспечивая высокий уровень защиты. Внутренняя часть защиты колена выполнена из пены средней плотности, которая обеспечивает отличную защиту и имеет легкий вес. Данный размер подходит примерно на возраст от 11 до 15 лет Характеристики Возрастная группа INT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/100/b17cf52e_4135_11e7_9c8f_3085a97fbce9_b17cf52f_4135_11e7_9c8f_3085a97fbce9.jpg?1557613911248040', null, '1.9', 2, 221, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_1.9_INT.png');
