INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (483, 'SR', '', 'Описание Трусы BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX, который отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Подкладка – THERMO MAX ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c7b/400_400_140cd750bba9870f18aada2478b24840a/616aca1c_8ce2_11e9_aa99_a4bf0158c9f5_6f7f5bc5_99de_11e9_aa9a_a4bf0158c9f5.jpg?158330450437843', null, 'SUPREME S29', 1, 485, 9, 'C:\Users\vinto/images/Трусы/BAUER_SUPREME S29_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (610, 'JR', '', 'Описание Клюшка CCM TACKS 9040, любительская модель линейки TACKS 2018 года. Данная модель идеально подходит для игроков любителей. Клюшка со средней точкой прогиба позволяет рукоятке прогибаться с большей амплитудой, что дает возможность накапливать максимум энергии для придания каждому броску силы. Материалом клюшки является углеродное волокно, которое обеспечивает долговечность и легкость. Крюк ULTRA ATTACKFRAME обладает легким весом и высокой жесткостью для более резких и точных бросков. Длина производителя - 49 дюймов Полная длина клюшки - 140 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Средняя ✅Технология крюка – ULTRA ATTACKFRAME Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/800/400_400_140cd750bba9870f18aada2478b24840a/b55537ea_291a_11e9_8927_e0cb4ee6cf35_c8ac2a8f_2a9f_11e9_99b0_3085a97fbce9.jpg?158329853215713', null, 'TACKS 9040 GRIP', 2, 612, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_TACKS 9040 GRIP_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (725, 'SR', '', 'Описание Клюшка BAUER SUPREME ADV, ограниченная серия клюшек линейки SUPREME 2019 года. Представляем вашего вниманию самую легкую клюшку в истории SUPREME. Вес данной модели всего 375 грамм достигается за счет нового уникального семиугольного сечения трубки SONIC TAPER TECHNOLOGY. Новая геометрия тэйпера помогла не только снизить вес клюшки, а также добавить стабильности и мощи при каждом броске. По периметру всей клюшки используется технология углового переплетения карбона ACL. В крюке в качестве внутренней сердцевины используется суперлегкая жесткая пена, которая уменьшает вес крюка и повышает прочность. Так же для более быстрого владения шайбой в середине крюка используется амортизирующая пена. Клюшка имеет оптимизированную точку прогиба, позволяющая прикладывать минимум усилий для максимально мощного броска. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Основные особенности: ✅Уровень – Элитный ✅Точка прогиба – Верхняя, оптимизированная ✅Тэйпер - SONIC TAPER TECHNOLOGY ✅Материал трубы – ADVANCED CARBON LAYERING ✅Крюк - ADVANCED CARBON LAYERING; амортизирующая пена, для более быстрого владения; суперлегкая, жесткая пена, для прочности и минимального веса Внимание: На данный товар скидка не действует. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e85/400_400_140cd750bba9870f18aada2478b24840a/4861874d_ebfb_11e9_aa9a_a4bf0158c9f5_29db4d2a_74be_11ea_aa9c_a4bf0158c9f5.jpg?158619433820399', null, 'SUPREME ADV SERIES GRIP', 1, 727, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME ADV SERIES GRIP_SR.png');
