INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (622, 'SR', '', 'Описание Клюшка BAUER NSX, любительская модель линейки NSX 2018 года. Клюшка выполнена из углеродного однонаправленного волокна. Волокна карбона в конструкции имеют повышенное натяжение и нити не переплетены между собой, а уложены в одном направлении. Данная модель имеет среднюю точку прогиба. Внешнее покрытие крюка выполнено из углеродного однонаправленного волокна. Полиуретановый сердечник крюка обеспечивает отличное чувство шайбы, уменьшает уровень вибрации при ударах и увеличивает прочность крюка. Конструкция клюшки двухсоставная. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Средняя ✅Технология трубы – Карбон UD ✅Технология крюка – Карбон UD, BRIDGECORE TECHNOLOGY ✅Конструкция – двухсоставная ✅Геометрия шафта – Квадратная Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f30/400_400_140cd750bba9870f18aada2478b24840a/d9153966_8b2f_11e8_995e_3085a97fbce9_d9153967_8b2f_11e8_995e_3085a97fbce9.jpg?158327619815254', null, 'NSX GRIPTAC', 1, 624, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_NSX GRIPTAC_SR.png');
