INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (328, 'JR', '', 'Описание Наплечник CCM TACKS 4052, новинка сезона 2015/16, имеет удобную посадку и отличается хорошей мобильностью за счет многопанельной конструкции. Нагрудник выполнен из легкой, формованной, перфорированной пены Foam Armour и имеет сегментированное строение, которое позволяет игроку свободно двигаться и с легкостью сгибаться. Вентиляционные отверстия в пене создают постоянный приток воздуха во внутреннее пространство нагрудника. Плавающий, центральный щиток, защищающий грудину, выполнен из пены высокой плотности и имеет дополнительную, сегментированную вставку из ударопрочного полиэтилена. Плечевые чаши выполнены из формованной пены высокой плотности и имеют литые пластиковые вкладки, что создает эффективную защиту плечевых суставов. Элементы защиты бицепса выполнены из формованного, ударопрочного полиэтилена. Модель имеет съемный, сегментированный щиток, защищающий живот. Задняя часть нагрудника имеет так же двух панельную конструкцию, где плавающий щиток с пластиковой вставкой закрывает и защищает позвоночник. Нагрудник комфортно сидит на теле и плотность его посадки можно подобрать с помощью эластичных ремней на липучке, расположенных на боках. Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/056/400_400_140cd750bba9870f18aada2478b24840a/28f9b70b_0bd1_11e6_9fde_001fc6b58537_f2fe7ae2_180f_11e6_be99_b0939a30c1de.jpg?158331592441851', null, 'TACKS 4052', 2, 330, 4, 'C:\Users\vinto/images/Нагрудники/CCM_TACKS 4052_JR.png');
