INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (309, 'SR', '', 'Описание Налокотники WARRIOR ALPHA DX, топовая модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • SLING WRAP - Регулируемые ремни обеспечивают надежную фиксацию и максимальное удобство • DX SHELL – Пена EVA, спрессованная по технологии SHELL SKIN, добавлена в ключевые зоны, чтобы обеспечить дополнительный уровень защиты там, где это необходимо больше всего • 3-PIECE – Новая трехкомпонентная конструкция, обеспечивает естественное движение и максимальную подвижность локтя • WARTECH FNC LINER – Антибактериальная подкладка Polygiene обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/893/400_400_140cd750bba9870f18aada2478b24840a/8dff5eb4_b90b_11e9_aa9a_a4bf0158c9f5_545174c5_bbb4_11e9_aa9a_a4bf0158c9f5.jpg?158328582264375', null, 'ALPHA DX', 3, 311, 8, 'C:\Users\vinto/images/Налокотники/WARRIOR_ALPHA DX_SR.png');
