INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (644, 'SR', '', 'Описание Клюшка BAUER SUPREME TOTALONE MX3 GRIPTAC Топовая модель коллекции 2015 года. SUPREME TOTALONE МХ3 — клюшка профессионального уровня для бросков с максимальной амплитудой. Верхняя точка прогиба в сочетании с мягким крюком обеспечат невероятную силу броска и отличный баланс. Профиль прогиба: • Усиленный профиль прогиба SUPREME посередине Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: Основа крюка POWER SENSE CORE 2 - НОВИНКА • Профиль крюка Pure Shot Форма рукоятки: • Квадратная двояковогнутая рукоятка • Конусность рукоятки • Двойное сужение SUPREME Power Варианты захвата: • Покрытие GRIPTAC Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d10/400_400_140cd750bba9870f18aada2478b24840a/6b410797_0bd1_11e6_9fde_001fc6b58537_db59278f_3913_11e6_bec3_dba2b096c3df.jpg?158329382717866', null, 'SUPREME TOTALONE MX3 GRIPTAC', 1, 646, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME TOTALONE MX3 GRIPTAC_SR.png');
