INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (324, 'YTH', '', 'Описание Наплечник WARRIOR ALPHA DX YTH, детская модель линейки ALPHA 2019 года. Основные особенности: ✅ DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты ✅ PROTECTION – Многослойная пена обеспечивает легкий вес, защиту и подвижность ✅LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа YTH Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ffb/400_400_140cd750bba9870f18aada2478b24840a/0109f378_1ffa_11ea_aa9a_a4bf0158c9f5_0a01deba_2b01_11ea_aa9a_a4bf0158c9f5.jpg?158331620844302', null, 'ALPHA DX', 3, 326, 4, 'C:\Users\vinto/images/Нагрудники/WARRIOR_ALPHA DX_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (325, 'YTH', '', 'Описание Наплечник CCM TACKS 9040 YTH, детская любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для детей начинающего уровня. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP с использованием пластика, чтобы защитить сустав от ударов. Все защитные элементы наплечника выполнены из комбинации пен разной плотности, что дает больше удобства. Между элементами защиты присутствуют эластичные вставки, которые дают ребенку больше свободы движения. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пена средней плотности ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пена средней плотности ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/864/400_400_140cd750bba9870f18aada2478b24840a/dc5b2d18_78af_11e9_aa99_a4bf0158c9f5_47eacad5_7ad4_11e9_aa99_a4bf0158c9f5.jpg?158330846449401', null, 'TACKS 9040', 2, 327, 4, 'C:\Users\vinto/images/Нагрудники/CCM_TACKS 9040_YTH.png');
