INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (649, 'SR', '', 'Описание Клюшка BAUER VAPOR S16 X900 GRIP SR – это модель профессионального уровня сезона 2016 года обладает отличными игровыми характеристиками, долговечностью и легкостью. Конструкция клюшки FUSED ONE-PIECE. При изготовлении этой модели используется углеродное волокно марки PRO-SPEC 12K, которое на 10% прочнее марки 3K и обладает улучшенной отзывчивостью и более сниженным весом. Волокна карбона в конструкции имеют повышенное натяжение, которое создается за счет использования системы R3 RESIN SYSTEM, что обеспечивает более надежную конструкцию, повышенную прочность и лучшую передачу энергии во время броска. Геометрия трубы – округленные углы и вогнутые боковые стенки. Сечение трубы MICRO FEEL II обеспечивает удобный хват клюшки, улучшая контроль над шайбой. Переработанная технология QUICK RELEASE TAPER и низкая точка прогиба LOW-KICK POINT предлагают самый быстрый процесс загрузки клюшки и наиболее мощный бросок. Покрытие трубы GRIP обладает отличной цепкостью. Конструкция крюка разработана, чтобы усилить действие технологии QUICK RELEASE TAPER и придать еще большей мощности броску. В качестве внутреннего сердечника AERO FOAM 3 CORE используется жесткая, полимерная пена, которая имеет высокое соотношение прочности и легкости и обеспечивает отличный баланс. Внешнее покрытие крюка, выполненное из плетеного карбонового волокна 12K PRO-SPEC CARBON, обеспечивает превосходную прочность в нескольких направлениях с повышенной чувствительностью. Дополнительно усиленная точка соединения крюка и трубы, имеющая профиль PURE SHOT BLADE, препятствует раскрытию крюка, его скручиванию при бросках, что увеличивает точность и мощность броска. Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3d1/400_400_140cd750bba9870f18aada2478b24840a/5eb662a7_e937_1