INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (654, 'INT', '', 'Описание Клюшка BAUER VAPOR 2X TEAM, полупрофессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба. Труба клюшки выполнена из углеродного волокна CARBON UD. Конструкция трубы MICRO FEEL II с двояковогнутыми боковыми стенками и скругленными углами, обеспечивает игроку максимальное удобство. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Крюк клюшки выполнен по технологии MaxBalance, благодаря чему повышается прочность крюка. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – CARBON UD; Micro Feel II • Технология крюка – 3K CARBON; MaxBalance; AERO FOAM I • Конструкция – HIGH PERFORMANCE MOLD • Геометрия шафта – С закругленными краями Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b61/400_400_140cd750bba9870f18aada2478b24840a/27fb7c46_e5a5_11e9_aa9a_a4bf0158c9f5_a0fdc347_e684_11e9_aa9a_a4bf0158c9f5.jpg?158331146620140', null, 'VAPOR 2X TEAM GRIP', 1, 656, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR 2X TEAM GRIP_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (333, 'JR', '', 'Описание Наплечник EASTON SYNERGY 650, обладает анатомической посадкой, хорошим воздухообменом и сниженным весом. В результате применения в данной модели технологии Hyperskin нагрудник имеет плотную посадку и необходимую гибкость с высоким уровнем защиты. Центральный щиток, закрывающий грудину и солнечное сплетение, имеет систему защиты MDP, состоящую из нескольких слоев пены переменной плотности с жесткой пластиковой вставкой. Такая сложная конструкция эффективно рассеивает силу удара, надежно защищая жизненно важные участки тела. Нижняя часть центрального щитка выполнена из модернизированной, облегченной пены E-Cell HD, которая не только обеспечивает высокий уровень защиты и имеет малый вес, но за счет своего строения способствует отличной воздухопроницаемости защитной экипировки. Арочные элементы из пены высокой плотности эффективно защищают ключицы. Дополнительные элементы из пены E-Cell HD закрывают боковую часть груди. Торс игрока защищен сегментированной, защитной жилеткой из перфорированной пены. Нагрудник имеет съемный элемент защиты живота. Плечевые чаши выполнены из литой, облегченной пены E-Cell HD и усилены пластиковыми вкладками. Элементы защиты бицепса имеют регулируемую конструкцию. Задняя панель имеет сегментированную систему защиты MDP, в остальном строение задней части нагрудника аналогично фронтальной части. Внутренняя, влагоотталкивающая подкладка способствует быстрому высыханию защитного снаряжения. Нагрудник крепится на теле с помощью эластичных ремней с фиксацией на липучке Характеристики Возрастная группа JR Линейка SYNERGY', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/66a/400_400_140cd750bba9870f18aada2478b24840a/751edb0f_e6a9_11e6_9165_e0cb4ee6cf35_b1a15186_0314_11e7_9c6b_3085a97fbce9.jpg?158331162244454', null, 'SYNERGY 650', 12, 335, 4, 'C:\Users\vinto