INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (348, 'JR', '', 'Описание Наплечник BAUER NEXUS 1N топовая модель в линейке Nexus 16 года. Традиционная посадка отлично сочетается с мультисегментной конструкцией нагрудника, гарантируя игроку высокий уровень подвижности защитного снаряжения и надежную защиту. Базовый слой нагрудника выполнен из пены средней плотности и разделен на сегменты для максимальной гибкости. Для создания дополнительной защиты ребер и области почек нагрудник усилен сегментами из пены высокой плотности. Подвижный щиток, закрывающий грудину, скомбинирован из пены высокой плотности, пластиковой вставки и формованной пены, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. В плечевых чашах, выполненных из литого пластика, так же используется пена с технологией Seven+, которая располагается в центре и обеспечивает эффективную защиту суставов. Съемная защита живота выполнена из пены средней плотности. Ворот нагрудника имеет мягкую окантовку из легкой пены, которая не травмирует кожу. Литой, двухсоставной, термоформируемый элемент защиты бицепса имеет свободную конструкцию Flex, которая обеспечивает максимальную мобильность этого элемента. Регулируемый ремень позволяет подобрать оптимальную длину элемента защиты бицепса. В задней части нагрудника используются перекрывающиеся сегменты из пены высокой плотности, которые проходят вдоль позвоночника, обеспечивая защиту профессионального уровня. Центральный щиток, закрывающий позвоночник, имеет широкую форму, перекрывая область сегментированной пены высокой плотности в базовом слое, что гарантирует высокий уровень защиты. Область ключиц и лопаток покрыта арочными элементами Free Glide Arch, при изготовлении которых используется пена высокой плотности. Арочные элементы движутся отдельно от базового слоя, обеспечивая игроку полную свободу движений. Внутренняя подкладка, выполненная по технологии 37.5, использует тепло тела хоккеиста для ускорения процесса испарения влаги и создания комфортного для игрока микроклимата. Специальная обработка материала подкладки исключает появление неприятных запахов. Эластичные ремни крепления с фиксацией на липучке позволяют закрепить нагрудник на талии и элементы защиты бицепса на руках игрока. Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/086/400_400_140cd750bba9870f18aada2478b24840a/b02fdf63_53d3_11e6_9c01_3085a97fbce9_b02fdf64_53d3_11e6_9c01_3085a97fbce9.jpg?158331256053375', null, 'NEXUS 1N', 1, 350, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_NEXUS 1N_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (349, 'JR', '', 'Описание Наплечник CCM SUPER TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Для максимальной защиты груди и позвоночника используется пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – JDP ✅Защита ключицы – Пена средней плотности ✅Защита груди – Пластик ✅Передняя защита ребер – Пена средней плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена средней плотности Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/19d/400_400_140cd750bba9870f18aada2478b24840a/4f9f93fc_5c25_11e9_99c6_3085a97fbce9_6fbe5035_79b9_11e9_aa99_a4bf0158c9f5.jpg?158331042845873', null, 'TACKS 9060', 2, 351, 4, 'C:\Users\vinto/images/Нагрудники/CCM_TACKS 9060_JR.png');
